--
-- Estructura de tabla para la tabla `medneointravenoso`
--

DROP TABLE IF EXISTS `medneointravenoso`;
CREATE TABLE `medneointravenoso` (
  `id` int(11) NOT NULL,
  `nombre` varchar(200) NOT NULL,
  `subcategoria` int(11) NOT NULL,
  `presentacion` text NOT NULL,
  `uso` text NOT NULL,
  `dosis` text NOT NULL,
  `reconstitucion` text NOT NULL,
  `concdilucion` text NOT NULL,
  `tieminfusion` text NOT NULL,
  `estabilidad` text NOT NULL,
  `incositio` text NOT NULL,
  `cuidenfermeria` text NOT NULL,
  `usuario1` varchar(40) NOT NULL,
  `fechareg` timestamp NOT NULL DEFAULT current_timestamp(),
  `usuario2` varchar(40) DEFAULT NULL,
  `estado` tinyint(4) NOT NULL DEFAULT 1
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='Medicamentos Neonatos Intravenosos';

--
-- Volcado de datos para la tabla `medneointravenoso`
--

INSERT INTO `medneointravenoso` (`id`, `nombre`, `subcategoria`, `presentacion`, `uso`, `dosis`, `reconstitucion`, `concdilucion`, `tieminfusion`, `estabilidad`, `incositio`, `cuidenfermeria`, `usuario1`, `fechareg`, `usuario2`, `estado`) VALUES
(1, 'ADRENALINA', 3, 'Ampolla 1mg / 1ml', 'Hipotensión. Choque hipotensivo resistente a fluidos o tratamineto con dopamina.', '0.05 mcg/kg/min - 1 mcg/kg/min', 'N.A', 'Concentración permitida:  10 mcg/cc - 64 mcg/cc\r\nDiluir 3 ampollas en 47 cc de SSN al 0.9% para concentración de 60 mcg/cc.\r\nVolumen final 50 ml.', 'Infusión continua 0.05 mcg/kg/min - 1 mcg/kg/min\r\nVía central', '24 h', 'Aminofilina, ampicilina, micafungina y bicarbonato de sodio', '-Monitoreo continuo y control metabólico, induce hiperglucemia y taquicardia, puede producir arritmias cardiacas, hipertensión arterial sistémica y necrosis de piel.  \r\n-Titulación y descenso lento.\r\n-Produce hiperglicemia, tomar glucometría a la hora de iniciar la infusión y después cada 4-6 horas.\r\n-Sí es posible corregir acidosis antes de administrar. Administrar únicamente por CVC o PICC.\r\n-Puede producir flebitis.\r\n-La mezcla requiere equipo fotoprotector', 'admin', '2024-06-10 23:44:03', 'jramos', 1),
(2, 'AMIODARONA', 3, 'Ampolla 150mg/3ml', 'Taquicardia supraventricular', '5mcg/kg/min –15mcg/kg/min', 'N/A', 'Concentración permitida: 600 mcg/ ml a 1800mcg/ml Diluir 1 ampolla de 3ml (150mg) en 247cc de DAD 5% para concentración de (600mcg/cc) Volumen final 250ml', 'Infusión continua 5mcg/kg/min – 15mcg/kg/min por vía central', '24 h', 'Heparina, furosemida, aminofilina, bicarbonato de sodio, sulfato demagnesio y cefazolina', '-Diluir UNICAMENTE EN DEXTROSA y administrar por catéter venoso central. \r\n-Alto riesgo de prolongación del intervalo QT y taquicardia ventricular por esto requiere monitoreo electrocardiográfico permanente. \r\n-Puede producir flebitis.\r\n-La mezcla para infusión continua requiere equipo fotoprotector para evitar adhisión.', 'jramos', '2024-10-08 15:58:27', NULL, 1),
(3, 'DOPAMINA', 3, 'Ampolla 200mg/5ml', 'Soporte hemodinámico \r\n-Bajas dosis: 1-5 mcg/kg/minuto: aumenta flujo sanguíneo renal y gasto urinario. \r\n-Dosis intermedia : 5-15 mcg/kg/minuto: aumenta flujo sanguíneo renal, ritmo cardiaco, contractibilidad cardiaca, gasto cardiaco y presión arterial.\r\n-Dosis altas: &gt;15 mcg/kg/minuto: Predominan los efectos adrenérgicos, vasoconstriccion, aumento de presión arterial.', '1mcg/kg/min – 20mcg/kg/min', 'N/A', 'Concentración permitida: 200mcg/ml - 800mcg/ml\r\n\r\n-PACIENTE PESO &lt; 2000gr: Diluir 1.3cc de la ampolla (50mg) en 98.7cc de solución salina 0.9% para concentración de (500mcg/cc)\r\n-PACIENTE PESO &gt; 2000gr: Diluir 2cc de la ampolla (80mg) en 98cc solución salina 0.9% para concentración de (800mcg/cc) \r\nVolumen final 100ml', 'Infusión continua 1mcg/kg/min – 20mcg/kg/min por vía central', '24 h', 'Aciclovir, alteplasa, anfotericina B, ampicilina, cefepima, furosemida, indometacina, insulina,penicilina G y bicarbonato de sodio.', '-Monitorización continua de la frecuencia cardíaca por riesgo de taquicardia.\r\n-Evaluar la producción de orina y perfusión periférica periodicamente.\r\n-Administrar por CVC o PICC. \r\n-La mezcla requiere equipo fotoprotector.', 'jramos', '2024-10-08 16:01:11', NULL, 1),
(4, 'DOBUTAMINA', 3, 'Ampolla 250mg/20ml', 'Soporte hemodinámico', '2mcg/kg/min - 25mcg/kg/min', 'N/A', 'Concentración permitida: 500mcg/ml - 4000mcg/ml \r\n-PACIENTE PESO &lt; 2000gr: Diluir 4 cc de la ampolla (50mg) en 96 cc de Solución salina 0.9% para concentración de (500mcg/cc) \r\n-PACIENTE PESO &gt; 2000gr: Diluir 8cc de la ampolla (100mg) en 92 cc Solución salina 0.9% para concentración de (1000mcg/cc) \r\nVolumen final 100ml', 'Infusión continua 2mcg/kg/min - 25mcg/kg/min por vía central', '24 h', 'Aciclovir, alteplasa, aminofilina, cefepima, bumetanida, diazepam, digoxina, furosemida, ibuprofeno lisina, indometacina, micafungina, fenitoína, fitonadiona, piperacilina tazobactam y bicarbonato de sodio', '-Evaluar efectos adversos como hipertensión, taquiarritmias, isquemia miocárdica y fibrilación ventricular.\r\n-Durante la preparación puede adquirir tonalidad rosada sin alteraciones.\r\n-No administrar bolos.\r\n-Administrar por CVC o PICC.', 'jramos', '2024-10-08 16:03:40', NULL, 1),
(5, 'LIDOCAINA', 3, 'Ampolla 1% y 2% 10 mll', 'Arritmias ventriculares agudas, taquicardia y fibrilación ventriculares asociadas a infarto de miocardio, cateterismo cardíaco, cardioversión o cirugía cardíaca', '20mcg/kg/min – 50mcg/kg/min', 'N/A', 'Concentración permitida: 4000mcg/m. Tomar 1 ampolla (presentación 2%) y llevarla a 50mL de DAD 5% Volumen final 50ml', 'Infusión continua 20mcg/kg/min – 50mcg/kg/min por vía central', '24 h', 'Aciclovir, anfotericina B liposomal, anfotericina B, caspofungina, diazepam, ganciclovir, milrinona, fenobarbital, fenitoina, tiopental.', 'Monitorizar electrocardiográficamente vigilando arritmias cardiacas y bradicardia sinusal, tromboflebitis local puede ocurrir con infusiones prolongadas.', 'jramos', '2024-10-08 16:07:29', 'jramos', 1),
(6, 'MILRINONE', 3, 'Ampolla 10mg/10ml', 'Soporte hemodinámico (choque séptico, choque cardiogénico, falla cardiaca aguda descompensada) hipertensión pulmonar persistente en el recién nacido.', '-Dosis impregnación: 50mcg/kg -75mcg/kg \r\n-Mantenimiento: 0.2mcg/kg/min – 1mcg/kg/min', 'N/A', 'Concentración permitida: 50 mcg/ml - 200mcg/ml \r\n\r\n-PACIENTE PESO MENOR 2000gr: Diluir 5 cc de la ampolla (5mg) en 95 cc de solución salina 0.9% para concentración de (50mcg/cc) \r\n-PACIENTE PESO MAYOR 2000gr: Diluir la ampolla completa (10mg) en 90 cc solución salina 0.9% para concentración de (100mcg/cc) \r\nVolumen final 100ml.', 'Dosis inicial entre 15 - 60 minutos según orden médica. Infusión continua 0.2mcg/kg/min – 1mcg/kg/min por vía central', '24 h', 'Anfotericina B liposomal, diazepam, furosemida, imipenem, lidocaina, ondansetron, fenitoina.', 'Monitoreo continuo, hipotensión, dolor torácico y arritmias ventriculares. Produce cefalea. El uso concomitante con otros inotrópicos potencializa su efecto. Titulación y descenso lento', 'jramos', '2024-10-08 16:10:00', NULL, 1),
(7, 'NORADRENALINA', 3, 'Ampolla 4mg/4ml', 'Hipotensión. Soporte hemodinámico en choque séptico.', '0.05mcg/kg/min – 1mcg/kg/min', 'N/A', 'Concentración permitida: 4mcg/cc - 16mcg/cc \r\nDiluir 1 ampolla (4mg) en 246 cc para concentración de 16mcg/cc \r\nVolumen final 250ml', 'Infusión continua 0.05mcg/kg/min – 1mcg/kg/min por vía central', '12h - 24h con fotoprotección', 'Aminofilina, anfotericina B liposomal, diazepam, acido folio, ganciclovir, indometacina, fenobarbital, fenitoina, bicarbonato de sodio, tiopental.', 'Monitoreo continuo, produce taquicardia, vasoconstricción periférica severa (cutánea, mesentérica, renal), riesgo alto de extravasación revisar sito de inserción, puede causar necrosis por extravasación, evitar su uso por vena periférica. Titulación y descenso lento. Para la isquemia por extravasación se debe infiltrar el lugar inmediatamente con 10 a 15 ml de solución de cloruro de sodio que contenga de 5 a 10 mg de fentolamina', 'jramos', '2024-10-08 16:12:08', NULL, 1),
(8, 'VASOPRESINA', 3, 'Ampolla 20UI/1ml', 'Hipotensión que no responde a reanimación con líquidos. Fibrilación o taquicardia ventricular refractarias a la desfibrilación inicial.', '0.01 UI/kg/h – 0.04UI/kg/h', 'N/A', 'Concentración permitida: 0.04UI/ml Diluir 0,5mL de la ampolla (10 unidades) en 249.5ml para concentración de (0,04 UI/ml) Volumen final 250ml.', 'Infusión continua 0.01 UI/kg/h – 0.04UI/kg/h por vía central', '18 h', 'Anfotericina B, bicarbonato, dantrolene, diazepam, furosemida indometacina, fenitoína.', 'Monitoreo continuo, puede producir hipertensión, taquicardia y arritmias. Vigilar gasto urinario. Necrosis por extravasación, evitar su administración por vena periférica. Titulación y descenso lento. En caso de extravasación detenga la infusión, no lave la línea, aspire para extraer líquido extravasado, drene de manera manual y mantener la extremidad afectada elevada. Como antídoto utilizar fentolamina.', 'jramos', '2024-10-08 16:17:18', NULL, 1),
(9, 'FENTANIL', 4, '500 mcg/10cc', 'Analgesia y sedación', 'Administración en bolo: 1 mcg/kg- 4mcg/kg\r\nInfusión continua: 0.5mcg/kg/hr – 5mcg/kg/hr', 'N/A', 'Para bolos: Preparar un volumen final de 10ml de manera que en 1 ml de la mezcla se asegure la dosis a administrar al paciente. \r\n\r\nPara infusión continua: Diluir 4ml de la ampolla (200mcg) en 16ml de solución salina 0.9% para una concentración de (10mcg/ml) \r\nVolumen final 20ml.', 'Bolos: administrar entre 3 a 5 minutos.\r\nInfusión continua: administre dosis en infusiones intermitentes de 15 a 30 minutos o en infusión continua.', '24 h', 'Azitromicina, bicarbonato pantoprazol, fenitoina, trimetoprim/sulfametoxazol.', 'Monitoreo continuo, puede inducir hipotensión, bradicardia, depresión respiratoria, sedación profunda y disminución importante de la motilidad intestinal. Contraindicado en hipertensión endocraneana por disminución de la perfusión cerebral, e insuficiencia hepática y renal grave. Administración lenta de bolos (3 a 5 minutos), por riesgo de parálisis torácica (tórax en leño), si se presenta, administrar 0.1 mg/kg de naloxona como antídoto o un relajante muscular. Titulación y descenso lento.', 'jramos', '2024-10-08 16:19:59', NULL, 1),
(10, 'MIDAZOLAM', 4, 'Ampolla de 5mg/5cc', 'Anticonvulsivante', '0.06 a 0.4 mg/kg/hora.', 'N/A', 'Diluir 2cc de la ampolla (2000mcg) en 18mL de solución salina 0.9% para concentración de (100mcg/ml) \r\nVolumen final 20ml.', 'Infusión continua 0.06 a 0.4 mg/kg /hora.', '24 h', 'Aciclovir, aminofilina, anfotericina B, anfotericin B liposomal, ampicilina, ampicilina/sulbactam, dexametasona sódica, dobutamina, furosemida, ganciclovir, indometacina, omeprazol, fenobarbital, fenitoína, piperacilina/ tazobactam, bicarbonato sódico, tiopental, nutrición parenteral.', 'Monitoreo continuo, puede inducir depresión y paro respiratorio, bradicardia e hipotensión arterial. Garantizar adecuada volemia previo a la administración de bolos. El uso simultáneo con ranitidina retarda el metabolismo y eliminación de Midazolam. En caso de sobredosificación, revertir con flumazenil 0,2 mg cada minuto hasta máximo 1mg. Titulación y descenso lento.', 'jramos', '2024-10-08 16:21:26', NULL, 1),
(11, 'MORFINA', 4, 'Ampolla de 10mg/1cc', 'Analgesia. Síndrome de abstinencia neonatal y sedación', 'Dosis de carga 100 mcg / kg en 1 hora continuar en infusión continua a 10-20 mcg / kg / hora', 'N/A', 'Diluir 0.2cc de la ampolla (2000mcg) en 18 ml de solución salina 0.9% para concentración de (100mcg/ml) \r\nVolumen final 20ml', 'Administrar dosis de carga en 1 hora infusión continua 10-20 mcg / kg / hora', '24h requiere foto protección', 'Aciclovir, Anfotericina B,Anfotericina B liposomal, ganciclovir, indometacin, fenitoina, furosemida, insulina, ampicilina/sulbactam.', 'Monitoreo continuo, puede inducir depresión y paro respiratorio, bradicardia e hipotensión arterial. Produce: bradicardia, edema, enrojecimiento facial, hipotensión, taquicardia, convulsiones, dependencia con el uso prolongado, diarrea, náusea, vómito. Utilizar equipo fotoprotector para la administración', 'jramos', '2024-10-08 16:22:34', NULL, 1),
(12, 'AMIKACINA', 5, 'Ampolla 100 mg/2ml', 'Tratamiento de infecciones con organismos resistentes a gentamicina. Bactericida de amplio espectro usado en infecciones producidas por bacilos gramnegativos aerobios moderadamente activos frente a bacterias grampositivas.', '≤ 29 sem 0 a 7 días: 18mg/kg/dosis c/48 hrs. 8 - 28 días: 15 mg/kg/dosis c/36hrs. \r\n≥29 días: 15 mg/kg/dosis c/24 hrs \r\n30-34 semanas de gestación: 0-7 días: 18 mg/kg/dosis c/36 hrs. \r\n≥ 8 días: 15 mg/kg/dosis c/24 hrs \r\n≥ 35 semanas: Todos: 15 mg/kg/dosis c/24 hrs.', 'N/A', 'Concentración permitida: 5mg/ml Tomar 1ml (50mg) de la ampolla en 9ml de solución salina 0,9% para una concentración de (5mg/ml) \r\nVolumen final 10ml.', 'Administrar dosis en 60 minutos', '24 h', 'Anfotericina B, ampicilina, azitromicina, heparina (concentraciones superiores a 1 unidad / ml), imipenem / cilastatina, mezlocilina, nafcilina, oxacilina, fenitoína, propofol, tiopental y ticarcilina / clavulánico.', '-Antes y periódicamente durante su administración debe evaluarse la funciones renal, vestibular y auditiva. -Los recién nacidos son más susceptibles a sus efectos tóxicos. \r\n-Manténgase un estado de hidratación adecuado a fin de disminuir el riesgo de lesión renal. \r\n-No se recomienda su administración simultánea con otros medicamentos capaces de afectar la función auditiva (ácido etacrínico, manitol, furosemida) o renal (anfotericina B, vancomicina, cisplatino y ciclosporina, entre otros). \r\n-Produce flebitis', 'jramos', '2024-10-08 16:25:53', NULL, 1),
(13, 'AMPICILINA SÓDICA', 5, 'Polvo liofilizado para solución inyectable 1gr', 'Bactericida de amplio espectro, actúa frente bacterias grampositivas (activas frente a enterococo, no productor de betalactamasas, infecciones urinarias no complicadas, infecciones nasofaríngeas, respiratorias y óticas', 'Rango de dosis: 25-50mg/kg/dosis; \r\nMeningitis 100mg/kg/dosis. \r\n≤ 29 sem: 0 a 28 días: C/12 horas \r\n≤ 29 sem: &gt;28 días: C/8 horas \r\n30 - 36 sem: 0-14 días: C/12 horas. \r\n30 - 36 sem: &gt;14 días: C/8 horas. \r\n37 - 44 sem: 0-7 días: C/12 horss. \r\n37 a 44 sem: &gt;7 días: C/8 horas \r\n&gt;45 sem: Todos: C/6 horas.', 'Concentración: 100mg/mL. Reconstituir la ampolla en 10mL de agua estéril.', 'Concentración permitida: 30mg/ml \r\nTomar 3mL de la solución madre (300mg) en 7mL en Solución salina 0,9% para una concentración de (30mg/ml) \r\nVolumen final de 10 ml.', 'Administrar dosis en 60 minutos', '24 horas refrigerado y 12 horas temperatura ambiente', 'Aminofilina, anfotericina B liposomal, atropina, aminoglucosidos(amikacina, gentamicina), gluconato de calcio, cefazolina, dexametasona, fluconazol, furosemida, heparina, gentamicina, hidrocortisona, insulina, fenobarbital, vancomicina.', 'No se recomienda en los pacientes con mononucleosis infecciosa, u otras infecciones virales, por riego de erupción medicamentosa. Aunque en general se tolera bien y posee baja toxicidad, se aconseja que durante los tratamientos prolongados se evalúen periódicamente las funciones renal, hepática y hematopoyética. Administraciones rápidas puede causar convulsiones.', 'jramos', '2024-10-08 16:28:30', NULL, 1),
(14, 'AMPICILINA SULBACTAM', 5, 'Polvo liofilizado para solución inyectable 1 gr ampicilina más 0.5 gr de sulbactam', 'Infecciones de piel, infecciones intraabdominales, ginecológicas, respiratorias, amplio espectro para microorganismos que producen betalactamasa como S aureus, H influenzae, E. coli, Klebsiella, acinectobacter, enterobacter y anaerobios.', 'Neonatos pretérmino: 100 mg ampicilina/kg/día dividido en dosis cada 12 horas \r\n\r\nNeonatos a término: 100 mg ampicilina/kg/día dividido en dosis cada 8 horas', 'Concentración: 150mg/mL. \r\nReconstituir la ampolla en 10mL de agua estéril.', 'Concentración permitida: 45 mg/cc \r\nTomar 3cc de la solución madre (450mcg) en 7 cc de solución salina 0,9% para concentración de (45 mg/ml, es decir, 30mg ampicilina + 15mg sulbactam)', 'Administrar dosis en 60 minutos', 'Diluida en solución salina es estable por 72 hora refrigerada', 'Aminofilina, amiodarona, anfotericina B liposomal, ampicilina, atropina, gluconato de calcio, cefazolina, dexametasona, fluconazol, furosemida, heparina, hidrocortisona, insulina, metronidazol, morfina, fenobarbital, bicarbonato sodio, vancomicina.', 'NO DILUIR EN DAD5% \r\nContraindicada en pacientes alérgicos a las penicilinas o a las cefalosporinas, en pacientes con antecedentes de enfermedades alérgicas (asma, eccema, fiebre del heno), mononucleosis infecciosa, insuficiencia renal grave. Las reacciones leves a moderadas se tratan con antihistamínicos y, si es necesario, con corticosteroides. Las reacciones anafilactoides graves requieren de medidas de urgencia, como el uso de adrenalina, aminofilina, oxígeno, corticosteroides intravenosos y control de vías aéreas, incluyendo la intubación.', 'jramos', '2024-10-08 16:30:24', NULL, 1),
(15, 'CEFAZOLINA', 5, 'Polvo liofilizado para solución inyectable 1 gr', 'Antibacteriano bactericida contra organismos estafilococos y estreptococos en infecciones del tracto respiratorio, piel, tracto urinario, tracto biliar, genitales y septicemia. Profilaxis prequirúrgica.', '25 mg/kg/dosis IV: \r\n≤29 sem: 0 a 28 días: C/12hrs; &gt; 28 días: C/8horas \r\n30 - 36 sem: 0 a 14días: C/12 hrs; &gt;14 días: C/8horas \r\n37- 44 sem: 0 a 7 días: C/12 hrs; &gt;7 días: C/8horas \r\n&gt;45 sem: Todos: C/6 horas.', 'Concentración: 100mg/ml \r\nReconstituir la ampolla en 10ml de agua estéril.', 'Concentración permitida: 20mg/ml \r\nTomar 2cc de la solución madre (200mg) en 8ml de solución salina 0,9% para concentración de (20mg/ml)\r\nVolumen final 10ml.', 'Administrar dosis en 60 minutos', '12 horas ambiente, refrigerado 10 días', 'Ampicilina, metronidazol, penicilina sulbactam, vancomicina.', 'Administrar con precaución en pacientes con hipersensibilidad a penicilinas y en pacientes con disminución de la función renal. La administración prolongada de cefazolina podría resultar en sobreinfección bacteriana o fúngica (candidiasis). Los niveles altos de cefazolina, particularmente en fallo renal, pueden aumentar el riesgo de crisis en pacientes epilépticos con bajo umbral. Puede ocasionar flebitis.', 'jramos', '2024-10-08 16:32:23', NULL, 1),
(16, 'CEFEPIME', 5, 'Polvo liofilizado para solución inyectable 1 gr', 'Tratamiento para infecciones, respiratorias, de la piel, tracto urinario y septicemias. Osteomielitis.', 'Neonatos a término y prematuros&gt;14 días: 50mg/kg/dosis cada 12 hrs \r\nNeonatos a término y prematuros&lt;14 días:30 mg/kg/dosis cada 12 hrs. \r\nMeningitis o infecciones graves por Pseudomonas o Enterobacter: 50 mg/kg/dosis C/8hrs.', 'Concentración: 100mg/ml \r\nReconstituir la ampolla en 10ml de agua estéril.', 'Concentración permitida: 20mg/ml \r\nTomar 2cc de la solución madre (200mg) en 8ml de solución salina 0.9% para concentración de (20mg/ml). Volumen final 10ml.', 'Administrar dosis en 30 minutos', 'Estable a temperatura ambiente durante 24 horas entre 20°C y 25°C, refrigerada 7 días entre 2°C y 8°C', 'Gentamicina, amikacina, aminofilina, metronidazol, vancomicina.', 'Administrar con precaución en pacientes con hipersensibilidad a penicilinas. Su uso prolongado puede originar infecciones fúngicas o superinfección bacteriana. Controlar la función renal y hepática periódicos en pacientes tratados simultáneamente con diuréticos potentes o fármacos potencialmente nefrotóxicos (aminoglucósidos, polimixinas, cloranfenicol, colistina), en especial si padecen riesgo de enfermedad renal.', 'jramos', '2024-10-08 16:34:08', NULL, 1),
(17, 'FLUCONAZOL', 5, 'Solución inyectable 200mg/100ml', 'Tratamiento de candidiasis, meningitis por crytococcos.', 'Infecciones sistémicas y meningitis: \r\nDosis de carga: 25 mg/kg/dosis \r\nDosis de mantenimiento: 12 mg/kg/dosis. \r\nProfilaxis: 3 mg/kg/dosis \r\n≤ 29 sem: 0 - 14 días: C/72 horas; &gt; 14 días: C/48 hrs. 30 - 36 sem: 0 a 14 días: C48 hrs; &gt; 14 días: C/24hrs. 37 - 44 sem: 0 a 7 días: C/48 hrs; &gt; 7 días: C/24 hrs \r\n≥45 sem Todos: C/24 hrs.', 'N/A', 'Concentración permitida: 2mg/ml No diluir. Presentación comercial se encuentra en la concentración permitida.', 'Administrar dosis en 60 minutos. No exceder velocidad de 200mg/hora.', 'Mantener a temperatura ambiente, no refrigerar ni congelar. Usar foto protección', 'Anfotericina B, ampicilina, gluconato de calcio, cefotaxima, ceftazidima, ceftriaxona, cloranfenicol, clindamicina, digoxina, furosemida, imipenem, piperacilina y trimetoprim sulfa', 'RA: Vómitos, dolor abdominal, náuseas y diarrea. Monitorear la función hepática, realice cuadro hematico y pruebas de función lipidica , para la candidemia, controle los hemocultivos diariamente o cada dos días hasta que se elimine Candida', 'jramos', '2024-10-08 16:36:03', NULL, 1),
(18, 'GENTAMICINA', 5, 'Ampolla de 40mg/ml o 80mg/2ml', 'Infecciones por gram(-) : Pseudomona, Proteus, Serratia, E. coli, Gram positivos: Stafilococus, Infecciones del SNC, respiratorias, de piel, abdominales, tracto urinario, endocarditis.', 'Dosis: \r\n-Edad gestacional (≤ 29semanas) \r\nEdad posnatal ≤7 días: 5 mg/kg/dosis cada 48 horas\r\nEdad posnatal 8 a 28 días: 4 mg/kg/dosis cada 36 horas \r\nEdad posnatal ≥ 29 días: 4 mg/kg/dosis cada 24 horas\r\n\r\n-Edad gestacional (30 a 34 semanas):\r\nEdad posnatal ≤7 días: 4.5 mg/kg/dosis cada 36 horas Edad posnatal ≥8 días: 4 mg/kg/dosis cada 24 horas \r\n\r\n-Edad gestacional ≥35 semanas: Todos 4 mg/kg/dosis cada 24 horas.', 'N/A', 'Concentración permitida: 4mg/ml Diluir la ampolla de 40mg en 9ml de solución salina 0,9% para concentración de (4mg/ml) Volumen final 10ml.', 'Administrar dosis en 60 minutos', '24 h', 'cloranfenicol, clindamicina, digoxina, furosemida, imipenem, piperacilina y trimetoprim sulfa', 'Administrar con precaución en neonatos debido a su inmadurez renal que podría ocasionar una vida media prolongada; en pacientes con alteración renal previa, alteraciones auditivas (vestibular), hipocalcemia, miastenia gravis y en situaciones con depresión de la transmisión neuromuscular.\r\nEl riesgo de neurotoxicidad u ototoxicidad están aumentados en pacientes con alteración de la función renal, altas dosis de Gentamicina y/ ó terapia prolongada.\r\nRealizar control periódico de pruebas de función renal, cuadro hemático con diferencial. Mantener el paciente hidratado. Produce flebitis', 'jramos', '2024-10-08 16:38:51', NULL, 1),
(19, 'MEROPENEM', 5, 'Ampolla de 1gr', 'Infecciones por gérmenes gram - multirresistentes, gram positivos anaeróbicos y aeróbicos, uso en tratamiento de meningitis, infecciones del tracto respiratorio, renal, intraabdominal, piel.', 'Dosis general para infecciones por organismos sensibles:\r\n-Sepsis: 20 mg/kg/dosis: \r\n≤32 sem: 0 a 14 días: cada 12 horas \r\n≤32 sem: &gt; 14 días: cada 8 horas \r\n&gt; 32 sem: 0 a 7 días: cada 12 horas \r\n&gt;32 sem: &gt; 7 días: cada 8 horas \r\n\r\n-Meningitis e infecciones por Pseudomonas: 40 mg/kg/dosis cada 8 horas. \r\n\r\nSe recomienda 40 mg/kg/dosis cada 8 horas, en infusiones de 4horas de duración en combinación con otro antibiótico sensible.', 'N/A', 'Concentración: 50mg/ml Reconstituir la ampolla en 20cc agua estéril.', 'Administrar dosis en 60 minutos. En infusión prolongada 3 horas', 'Estabilidad según la concentración de la mezcla: \r\n*20 mg/mL: 1h a 2h temperatura ambiente o 15 horas en refrigeración.\r\n*14,3 mg/ml: hasta 7 horas a temperatura ambiente.\r\n*10 mg / ml: hasta 12 h a temperatura ambiente.', 'Aciclovir, dexametasona, fluconazol, furosemida, gentamicina, heparina, metoclopramida, fenobarbital, vancomicina.', 'Realizar seguimiento periódico de función renal, hepática y hematológica, cambios en la frecuencia y características de las deposiciones. Suspender ante reacción alérgica.', 'jramos', '2024-10-08 16:42:16', NULL, 1),
(20, 'PENICILINA G SÓDICA', 5, 'Ampolla de 5000000 UI', 'Tratamiento de sepsis, meningitis, pericarditis, endocarditis, neumonía, algunas infecciones por gérmenes gram (-). Tratamiento de sífilis congénita en neonatos.', 'Dosis general para infecciones por organismos sensibles: \r\n-Meningitis: 75.000 a 100.000 UI/kg/dosis \r\n-Bacteriemia: 25.000 a 50.000 UI/kg/dosis \r\n≥ 29 sem: 0 a 28 días: C/12 horas\r\n&gt; 28 días: C/8 horas \r\n30 a 36 sem: 0 a 14 días: C/12 horas; &gt;14 días C/8 horas.\r\n37 a 44 Sem: 0 a 7 días: C/12 horas; &gt;7 días C/ 8horas.\r\n≥ 45 semanas: Todos: C/6 horas.', 'N/A', 'Concentración: 100.000UI/ml Reconstituir la ampolla en 10mL de agua estéril.', 'Administrar dosis en 30 minutos', 'Estabilidad entre 2°C a 8°C por 72 horas, ambiente 12 horas.', 'Amikacina, gentamicina, fenobarbital, fenitoina, trimetropim sulfametozasol.', 'Vigilar electrolitos, Usar con precaución si trastorno convulsivo previo y alteración función renal/hepática, Observar paciente por signos de hipersensibilidad. Realizar control de cuadro hemático, uroanálisis, pruebas de función renal. Produce flebitis.', 'jramos', '2024-10-08 16:45:17', NULL, 1),
(21, 'PIPERACILINA TAZOBACTAM', 5, 'Solución inyectable 4gr de piperacilina y 0.5 gramos de tazobactam', 'Infecciones respiratorias inferiores, infección de vías urinarias, infección intraabdominal de piel y tejido blando, septicemias y bacteriemias por gram (+) y gram (-)', 'Para infecciones fuera del SNC: 50 a 100 mg piperacilina/kg/dosis, intervalos de acuerdo a lo siguiente: \r\n≥ 29 sem: 0 a 28 días: C/12 horas; &gt; 28 días: C/8 horas\r\n30 a 36 sem: 0 a 14 días: C/12 horas; &gt; 14 días C/8 horas\r\n37 a 44 Sem: 0 a 7 días: C/12 horas; &gt; 7 días C/ 8horas. ≥ 45 semanas: Todos: C/8 horas.x', 'N/A', 'Concentración: 200mg/ml Reconstituir la ampolla en 20cc de agua estéril.', 'Administrar dosis en 2 - 4 horas', '24 h', 'Aciclovir, gentamicina, insulina, vancomicina.', 'Evaluación de electrolitos, función renal, hepática y hematopoyética periódica en tratamiento prolongado. Produce flebitis', 'jramos', '2024-10-08 16:46:40', NULL, 1),
(22, 'VANCOMICINA', 5, 'Ampolla de 500mg', 'Acción bactericida, interfiere con la síntesis de la pared celular, inhibe la síntesis del RNA, y altera la función de la membrana plasmática. En infecciones causadas por estafilococo meticilino resistente) S. áureus, S. epidermidis) y neumococo penicilina resistente.', '*29 semanas EPM o menos 0-14 días de nacido 10 a 15 mg/kg/dosis c/d 18H &gt;14 días de nacido 10 a 15 mg/kg/dosis c/d12H \r\n\r\n*30 a 36 semanas EPM 0-14 días de nacido 10 a 15 mg/kg/dosis c/d 12H &gt;14 días de nacido 10 a 15 mg/kg/dosis c/d8H, 36 a 44 semanas EPM 0-7 días de nacido 10 a 15 mg/kg/dosis c/d 12H &gt;7 días de nacido 10 a 15 mg/kg/dosis c/d8H &gt;=45 semanas EPM \r\n\r\nTodas 10 a 15 mg/kg/dosis c/d 6H', 'Concentración: 50mg/ml Reconstituir la ampolla en 10mL de agua estéril o solución salina 0.9%', 'Concentración permitida: 5mg/ml\r\n\r\nTomar el volumen total de la reconstitución (10cc) en 90 ml de solución salina 0,9% para concentración de (5mg/ml) \r\nVolumen final 100ml.', 'Administrar dosis en 2 horas.', 'Estable bajo refrigeración por 14 días y al ambiente por 24 horas.', 'Aminofilina, anfotericina B liposomal, ampicilina, furosemida, heparina, hidrocortisona, fenitoína, penicilina sulbactam, piperacilina tazobactam', 'Observar el sitio de punción para detectar signos de extravasación, puede haber necrosis y desprendimiento del tejido. Si se administran otros ototóxicos monitorizar función auditiva. monitorear función renal, realizar cuadro hemático. Produce flebitis', 'jramos', '2024-10-08 16:49:40', NULL, 1);

