-- Estructura de tabla para la tabla `medpedintravenoso`
--

DROP TABLE IF EXISTS `medpedintravenoso`;
CREATE TABLE `medpedintravenoso` (
  `id` int(11) NOT NULL,
  `nombre` varchar(200) NOT NULL,
  `subcategoria` int(11) NOT NULL,
  `agrupamiento` int(11) DEFAULT NULL,
  `presentacion` text NOT NULL,
  `uso` text NOT NULL,
  `dosis` text NOT NULL,
  `reconstitucion` text NOT NULL,
  `condilucion` text NOT NULL,
  `tieinfusion` text NOT NULL,
  `incsitio` text NOT NULL,
  `cuipreparacion` text NOT NULL,
  `cuiadministracion` text NOT NULL,
  `usuario1` varchar(40) NOT NULL,
  `fechareg` timestamp NOT NULL DEFAULT current_timestamp(),
  `usuario2` varchar(40) DEFAULT NULL,
  `estado` tinyint(4) NOT NULL DEFAULT 1,
  `dilucionmin` text NOT NULL,
  `dilucionmax` text NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='Medicamentos Pediatria Intravenosos';

--
-- Volcado de datos para la tabla `medpedintravenoso`
--

INSERT INTO `medpedintravenoso` (`id`, `nombre`, `subcategoria`, `agrupamiento`, `presentacion`, `uso`, `dosis`, `reconstitucion`, `condilucion`, `tieinfusion`, `incsitio`, `cuipreparacion`, `cuiadministracion`, `usuario1`, `fechareg`, `usuario2`, `estado`, `dilucionmin`, `dilucionmax`) VALUES
(1, 'ÁCIDO TRANEXAMICO', 13, 18, 'Ampolla 500 mg / 5ml', 'Uso a corto plazo (2 a 8 días) en pacientes con hemofilia para reducir o prevenir hemorragias y disminuir la pérdida de sangre perioperatoria y la necesidad de transfusión.', 'Dosis permitida:\r\n10 - 15 mg/kg/dosis cada 8 horas', 'N.A', 'Concentración permitida: 6 mg/ml', 'Administrar de 5 a 30 minutos', 'Ampicilina, ampicilina/sulbactam, penicilina G, piperacilina, piperacilina/tazobactam, ticarcilina/clavulanato. Complejo anticoagulante.', '- Proteger de la luz\r\n\r\n- Usar inmediatamente después de diluido', 'Vigilar función renal, hepática y agudeza visual.', 'dhernandez', '2024-05-27 20:44:46', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\nEjemplo: \r\nNiño 15 kg\r\n150 mg / 6 (mg/ml) = 25 ml (volumen de dilución mínimo)\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'NA'),
(2, 'ADENOSINA', 13, 19, 'Ampolla 6 mg / 2 ml', 'Indicada en el manejo de taquicardia supraventricular paroxistica o para diagnóstico diferencial en la arritmia ventricular regular.', 'Primera dosis: 0,1 mg/kg Máximo 6 mg\r\nSegunda dosis: 0,2 mg/kg  Máximo 12 mg', 'N.A', 'Puro', '1 - 2 segundos', 'Digoxina, verapamilo, cafeina, teofilina.', '- Almacenar a temperatura ambiente.\r\n- No refrigerar, ocurrirá la cristalización. \r\n\r\n- La solución debe ser clara al momento de su uso.', '- Se debe realizar una monitorización continua del ECG y la presión arterial.\r\n\r\n- Monitoreae los signos vitales\r\n\r\n- Acceso venoso de buen calibre.\r\n\r\n- Lavar después de administrar.\r\n\r\n- Disponibilidad en el carro de paro.', 'dhernandez', '2024-05-27 20:55:20', 'jramos', 1, 'NO SE DILUYE\r\nProcedimiento para la administración:\r\n1. Obtener acceso venoso: en ausencia de catéter venoso central, obtener acceso periférico de gran calibre en la porción proximal del brazo o la fosa antecubital izquierda.\r\n2. Conectar llave de tres vías: Conectar directamente al puerto del catéter una llave de tres vías. En el puerto principal una jeringa de 3 o 5 ml con el medicamento puro, y en el puerto auxiliar una jeringa de 20 ml con solución salina para lavar. \r\n3. Administrar el medicamento: Este procedimiento requiere de la participación de dos personas. La primera administra el medicamento y controla la llave de tres vías, la segunda, eleva el brazo y realiza el lavado con solución salina. Todo el procedimiento se realiza a la máxima velocidad posible. \r\nLactantes y niños menores de 5 años: lavar con 5 ml\r\nNiños mayores de 5 años: Lavar con 10 ml\r\nAdolescentes: 20 ml', 'NA'),
(3, 'AMIODARONA', 13, 19, 'Ampolla 150 mg / 3ml', 'Antiarritmico', 'Bolo: 5 mg/kg \r\nInfusión continua: 5 - 15 mcg/kg/min', 'N.A', 'Concentración máxima permitida por ml de la mezcla para infundir: \r\nVía periférica: 2 mg/ml\r\nVía central 6 mg/ml\r\n\r\nDiluir ÚNICAMENTE en Dextrosa 5%', 'Bolo: 30 a 60 minutos\r\nInfusión continua: según dosis deseada\r\nml/h= dosis en mcg x peso en kg x 60 min / mcg del medicamento por ml\r\nejemplo: Usuario 15 kg dosis prescrita: 5 mcg/kg/min\r\nml/h= 5x15x60/2.000 = 2.2 ml/h', 'Ampicilina, ceftazidima, cefazolina, digoxina, heparina, imipenem-cilastatina, mezlocilina, piperacilina/tazobactam, bicarbonato de sodio y nitroprusiato de sodio.', '- Estabilidad de 24 horas\r\n\r\n- Requiere equipo fotoprotector para infusión continúa y filtro\r\n\r\n- Es incompatible con el pvc.', '- Monitoreo electrocardiográfico permanente. Alto riesgo se prolongación del intervalo QT y taquicardia ventricular tipo torsión de puntas\r\n- Puede producir flebitis. Evitar concentraciones superiores a los 2 mg/ml por vena periférica. Emplear vena de gran calibre con catéter # 24 o 22 en niños mayores de 10 años.', 'dhernandez', '2024-05-27 21:06:39', 'jramos', 1, 'Bolo: diluir a una concentración máxima de 2 mg/ml\r\nInfusión continua: 2 ml (100 mg) + 48 DAD 5% \r\nConcentración: 2.000 mcg/ml', 'NA'),
(4, 'ALBUMINA HUMANA', 13, 19, 'Ampolla 20 % / 50 ml', 'Tratamiento para anemia hemolitica en recién nacidos, hipovolemia en pacientes pediátricos, expansión del volumen plasmático y mantenimiento del gasto cardíaco en el tratamiento de ciertos tipos de shock, Hipoalbuminemia.', 'Dosis permitida: 0,5 - 1 gr/kg/dosis', 'N.A', 'Concentración permitida del 25% \r\n1 - 2 ml/min. \r\n\r\nDiluir en Dextrosa 5%', '- Shock: 30 a 60 minutos\r\n- Hipoproteinemia: 4 horas', 'Midazolam, vancomicina, verapamilo', '- Administrar en las 4 horas siguientes.\r\n\r\n- No utilizar si la solución está turbia o contiene sedimentos\r\n\r\n- No emplear agua esteril para inyección, para diluir la albúmina', 'Vigile de cerca los parámetros hemodinámicos después de la administración para detectar evidencia de insuficiencia cardíaca o respiratoria, insuficiencia renal o aumento de la presión intracraneal.', 'dhernandez', '2024-05-27 21:21:27', 'jramos', 1, 'pura', 'NA'),
(5, 'DEXAMETASONA MEDICAMENTO LASA', 13, 20, 'Ampolla 8 mg / 2 ml', 'Corticoide sistemico: antiinflamatorio, corticoesteroide y glucocorticoide.', '0,3 a 0,6 mg/kg/dosis.', 'N.A', 'Concentración permitida: 4 mg/ml\r\n\r\nDiluir en SSN 0,9 % o Dextrosa 5 %', '30 minutos.', 'Midazolam y vancomicina', 'Estabilidad a temperatura ambiente por 24 horas.', '- Vigile la tensión arterial y la glucosa en sangre durante una enfermedad aguda.\r\n\r\n- Controle los electrolitos\r\n\r\n- Controle el estado de crecimiento (especialmente el crecimiento lineal) y la presión intraocular en niños que reciben terapia a largo plazo.\r\n\r\n- Observe si hay signos de infección.', 'dhernandez', '2024-05-27 21:30:14', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\nEjemplo: \r\nNiño 15 kg\r\n4.5 mg / 4 (mg/ml) = 1.5 ml (volumen de dilución mínimo)\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(6, 'HIDROCORTISONA SUCCINATO', 13, 20, 'Ampolla 100 mg / 2 ml', 'Insuficiencia adrenal primaria y secundaria, antiinflamatorio e inmunosupresor en el tratamiento de enfermedades variadas de origen alérgico, hematológico, dermatológico, gastrointestinal, oftalmológico, reumatológico, neoplásico, autoinmune, sistema nervioso central, respiratorio y renal. Uso en shock séptico.', 'Bolo de 1-2 mg/kg/dosis\r\nMantenimiento: 25 a 150 mg/kg/día dividido en 3 a 4 dosis\r\nInmunosupresor:  1 a 5 mg/kg/día', '10 ml de SSN 0,9 %', 'Concentración máxima permitida por ml de la mezcla para infundir: 5 mg/ml', '1 hora', 'Aminoglutetimida, antiácidos, antifúngicos azólicos , barbitúricos, antibióticos macrólidos (excepto azitromicina, fidaxomicina y espiramicina), primidona y los derivados de rifamicina.', '- Una vez reconstituido, se debe conservar refrigerado (2-8 °C)\r\n\r\n- Usar en menos de 24 horas.', '- Vigilar signos vitales\r\n- Estado mental del paciente\r\n- Reacciones cutáneas, metabólicas y gastrointestinales. \r\n- Vigilar función hepática y hematológica.', 'dhernandez', '2024-05-27 21:44:34', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\nEjemplo: \r\nNiño 15 kg\r\n125 mg / 5 (mg/ml) = 25 ml (volumen de dilución mínimo)\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(7, 'METILPREDNISOLONA', 13, 20, 'Polvo liofilizado para solución inyectable 500 mg', 'Glucocorticoide de administración sistémica (oral, intramuscular [i.m.] o endovenosa [e.v.]) o tópica con acción antiinﬂamatoria y/o inmunosupresora.', '1-2 mg/kg/día dividido en 3 a 4 dosis', 'SSN', 'Concentración máxima permitida por ml de la mezcla para infundir: 2 mg/ml\r\n\r\nDextrosa al 5% o SSN 0,9%.', '1 hora', 'Anfotericina B. Claritromicina, antiinflamatorios, salicilatos', '- Estable durante 48 horas. \r\n- Consérvese a temperatura ambiente a no más de 30° C y en lugar seco.', 'Vigilar reacciones gastrointestinales, metabólicas, renales y cardiovasculares.\r\nVigilar niveles de corticoide en sangre', 'dhernandez', '2024-05-27 21:49:11', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\nEjemplo: \r\nNiño 15 kg\r\n15 mg / 2 (mg/ml) = 7.5 ml (volumen de dilución mínimo)\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(8, 'DIPIRONA', 13, 21, 'Ampolla 1 gr / 2 ml', '- Tratamiento del dolor agudo posoperatorio o postraumático.\r\n- Dolor de tipo cólico.\r\n- Dolor de origen tumoral.\r\n- Fiebre alta que no responde a otros antitérmicos.', '20 a 40 mg/kg/dosis', 'N.A', 'Concentración máxima permitida por ml de la mezcla para infundir: 20 mg/ml\r\n\r\nDiluir en SSN 0,9 % o DAD 5%', '30 - 60 minutos', 'NO ADMINISTRAR CON OTROS MEDICAMENTOS. Lavar la vía antes y después de su administración', 'Diluida es estable por 24 horas', 'Vigilar sudoración, frío, vértigo, obnubilación, náuseas, palidez cutánea y dificultad respiratoria.   Si se administra por esta vía rápidamente puede producir hipotensión, sofoco, rubor, palpitaciones y náusea.', 'dhernandez', '2024-05-27 21:56:27', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\nEjemplo: \r\nNiño 15 kg\r\n300 mg / 20(mg/ml) = 15 ml (volumen de dilución mínimo)\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(9, 'DOPAMINA MEDICAMENTO LASA', 13, 22, 'Ampolla 200 mg / 5 ml', 'Soporte hemodinámico', 'Infusión continua de  5 - 20 mcg/kg/minuto', 'N.A', 'Concentración máxima permitida por ml de la mezcla para infundir: 3.200 mcg/ml', 'Infusión continua: según dosis deseada\r\nml/h= dosis en mcg x peso en kg x 60 min / mcg del medicamento por ml\r\nejemplo: Usuario 15 kg dosis prescrita: 20 mcg/kg/min\r\nml/h= 5x15x60/2.400 = 1.8 ml/h', 'Aciclovir, alteplasa, anfotericina B, ampicilina, cefepima, furosemida, indometacina, insulina,penicilina G y bicarbonato de sodio.', '- Estabilidad por 24 horas.\r\n- Administrar por CVC o PICC. \r\n- La mezcla requiere equipo fotoprotector.', 'Monitoreo continuo, puede causar taquicardia, ectopias y arritmias, hipertensión, dolor\r\ntorácico e hipoperfusión intensa con necrosis de miembros inferiores a dosis altas.\r\nAdministrar únicamente por catéter venoso central. El uso concomitante con\r\nfenitoína puede causar convulsiones, hipotensión grave y bradicardia. Titulación y\r\ndescenso lento.', 'dhernandez', '2024-05-27 22:12:57', 'jramos', 1, 'Infusión continua:\r\n&lt;10 kg: 1 ml (40 mg) + 49 ml SSN\r\nConcentración: 800 mcg/ml\r\n&gt;10 kg: 3 ml (12 mg) + 47 ml SSN\r\nConcentración: 2.400 mcg/ml', 'NA'),
(10, 'Adrenalina MEDICAMENTO LASA', 13, 22, 'Ampolla 1 mg / 1 ml', 'vasopresor e inotrópico. \r\nHipotensión, estado post reanimación y bradicardia.', 'Bolo en reanimación: 0,01 mg/kg cada 3 minutos\r\nInfusión continua 0.1 mcg- 1 mcg/Kg/minuto.', 'N.A', 'Concentración máxima permitida por ml de la mezcla para infundir: 64 mcg/ml', 'Bolo: 1 minuto\r\nInfusión continua: según dosis deseada\r\nml/h= dosis en mcg x peso en kg x 60 min / mcg del medicamento por ml\r\nejemplo: Usuario 15 kg dosis prescrita: 0.1 mcg/kg/min\r\nml/h= 0.1x15x60/60 = 1.5 ml/h', 'Aminofilina, ampicilina, hialuronidasa, micafungina y bicarbonato de sodio.', '- Almacenar a temperatura ambiente. \r\n\r\n- No utilizar si la solución cambia de color o se produce precipitación.\r\n\r\n- Administrar por Cateter venoso central.', 'Monitoreo continuo y control metabólico, induce hiperglucemia y taquicardia, puede producir arritmias cardiacas, hipertensión arterial sistémica y necrosis de piel. Titulación y descenso lento.\r\nProduce hiperglicemia, tomar glucometría a la hora de iniciar la infusión y después cada 4-6 horas.', 'dhernandez', '2024-05-27 22:19:09', 'jramos', 1, 'Bolo para reanimación: diluir 1 mg en 10 ml de SSN (1 ml=0,1 mg) administrar 0.1 ml por kg de peso\r\nInfusión continua:\r\n&lt;10 kg:  2 ml (2 mg) + 48 ml SSN\r\nConcentración: 40 mcg/ml\r\n&gt;10 kg: 3 ml (3 mg) + 47 ml SSN\r\nConcentración: 60 mcg/ml', 'NA'),
(11, 'MILRINONE', 13, 22, 'Ampolla 10 mg / 10 ml', 'Inotrópico y vasodilatador', 'Dosis impregnación:\r\n50 mcg/kg en 15 min.\r\n\r\nInfusión continua: 0,25 a 1 mcg/kg/min', 'N.A', 'Concentración máxima permitida: 200 mcg/ml \r\n\r\nDiluir con SSN 0,45%, SSN 0,9% O DAD 5%', 'Bolo: 15 minutos\r\nInfusión continua: según dosis deseada\r\nml/h= dosis en mcg x peso en kg x 60 min / mcg del medicamento por ml\r\nejemplo: Usuario 15 kg dosis prescrita: 0.25 mcg/kg/min\r\nml/h= 0.25x15x60/200 = 1.1 ml/h', 'Anfotericina B liposomal, diazepam, furosemida, imipenem, lidocaina, ondansetron, fenitoina.', '- Purgar el equipo con la mezcla ya preparada\r\n- Vigilar el fenómeno de sorción con el pvc después de las 72 horas.', '-Monitoreo continuo, hipotensión, dolor torácico y arritmias ventriculares. Produce cefalea.El uso concomitante con otros inotrópicos potencializa su efecto. Titulación y descenso lento', 'dhernandez', '2024-05-27 22:22:40', 'jramos', 1, 'Bolo: Diluir en 30 ml\r\nInfusión continua: 10 ml (10 mg) + 40 ml de SSN\r\nConcentración: 200 mcg/ml', 'NA'),
(12, 'NORADRENALINA', 13, 22, 'Ampolla 4 mg / 4 ml', 'Vasoactivo e inotrópico', 'Infusión continua: 0.05 mcg/kg/min – 0.5 mcg/kg/min por vía central', 'N.A', 'Concentración máxima permitida por ml de la mezcla para infundir: 60 mcg/ml\r\n\r\nDiluir en DAD 5% para una estabilidad de 12 horas \r\nEn SSN solo es estable por 6 horas', 'Infusión continua: según dosis deseada\r\nml/h= dosis en mcg x peso en kg x 60 min / mcg del medicamento por ml\r\n\r\nEjemplo: Usuario 15 kg dosis prescrita: 0.05 mcg/kg/min\r\nml/h= 0.05x15x60/40 = 1.1 ml/h', 'Aminofilina, anfotericina B liposomal, diazepam, acido folio, ganciclovir, indometacina, fenobarbital, fenitoina, \r\nbicarbonato de sodio, tiopental.', '- Las ampollas se conservan a temperatura ambiente. \r\n\r\n- La dilución es estable 24 h conservada a temperatura ambiente. \r\n\r\n- Se oxida fácilmente; no utilizar si la coloración es parda.', 'Monitoreo continuo, produce taquicardia, vasoconstricción periférica severa (cutánea, mesentérica, renal), necrosis por extravasación, evitar su uso por vena periférica. Titulación y descenso lento.\r\nAdministrar ÚNICAMENTE POR CATÉTER VENOSO CENTRAL pues su\r\nextravasación puede causar necrosis extensa, en caso de requerir iniciar la infusión por vena periférica, elija una vena de gran calibre e inserte un catéter # 24 0 22 en niños grandes (mayores de 10 años). En caso de extravasación: infiltrar la zona inmediatamente con 5 mg de Fentolamina diluidos en 10 ml de solución salina.', 'dhernandez', '2024-05-27 22:28:23', 'jramos', 1, 'Infusión continua:\r\nMenores de 10 kg: 1 ml (1 mg) + 49 ml de DAD 5%\r\nConcentración: 20 mcg/ml\r\nMayores de 10 kg:2 ml (2 mg) + 48 ml de DAD 5%\r\nConcentración: 40 mcg/ml', 'NA'),
(13, 'FENITOINA SODICA', 13, 23, 'Ampolla 250 mg / 5 ml', 'Tratamiento del estatus epiléptico de tipo tónico-clónico.\r\nCrisis tónico-clónicas generalizadas y crisis parciales simples o complejas.\r\nTratamiento y prevención de las convulsiones en neurocirugía.\r\nArritmias auriculares y ventriculares, especialmente cuando están causadas por intoxicación digitálica', 'Dosis de carga: 15 - 20 mg/kg dosis única\r\n            \r\nDosis de mantenimiento: 5 mg/kg/día dividida en 3 dosis\r\n\r\nDosis máxima: 1,5 gr/día', 'N.A', 'Concentración permitida: 2 mg/ml\r\n\r\nSSN 0,9%', '1 hora', 'Carbamazepina, isoniazida, diazepam, amiodarona, fenobarbital, Ácido valproico, reserpina, diazóxido, ácido fólico y sucralfato.', '- Almacenarlo a temperatura inferior a 30 °C, proteger de la luz y usar en menos de 22 horas. \r\n\r\n- Seleccionar vena de gran calibre con catéter 22 o 24. \r\n\r\n- Lavar con SSN antes y después de su administración.', '- Monitorizar electrocardiograma, la presión arterial y la función respiratoria continuamente durante la infusión y durante 15 minutos a 1 hora después de la infusión. \r\n\r\n- Altamente vesicante. Diluir en volumen máximo permitido.', 'dhernandez', '2024-05-27 22:35:40', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\nEjemplo: \r\nNiño 15 kg\r\nDosis de carga: 225 mg / 2 (mg/ml) = 112.5 ml (volumen de dilución mínimo)\r\nMantenimiento 75 mg / 2 (mg/ml) = 37.5 ml (volumen de dilución mínimo)\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(14, 'LEVETIRACETAM', 13, 23, 'Ampolla 500 mg / 5 ml', 'anticonvulsivante', '10-20 mg/kg/dosis cada 12 horas', 'N.A', 'Concentración permitida: 5 mg/ml\r\n\r\nDiluir en SSN 0,9%', '15 - 30 minutos', 'N.A', '- La solución reconstituida es estable, al menos 24 h a 15-25 °C. \r\n\r\n- Suele ser incompatible con el PVC.', 'Vigilar  somnoliencia, cefalea, mareos, trombocitopenia y pancitopenia.', 'dhernandez', '2024-05-27 22:45:05', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\nEjemplo: \r\nNiño 15 kg\r\n150 mg / 5(mg/ml) = 30 ml (volumen de dilución mínimo)\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(15, 'MIDAZOLAM MEDICAMENTO LASA', 13, 23, 'Ampolla 5 mg / 5 ml', 'Sedante', 'Bolo: 0,05 a 0,1 mg/ kg                                  \r\nInfusión continua: 1 - 5 mcg/kg/min', 'N.A', 'Concentración máxima permitida: 1 mg/mL', 'Bolo: 5 a 10 minutos\r\nInfusión continua: según dosis deseada\r\nml/h= dosis en mcg x peso en kg x 60 minutos / mcg del medicamento por ml\r\nejemplo: Usuario 15 kg dosis prescrita: 1 mcg/kg/min\r\nml/h= 1x15x60/1.000 = 0.9  ml/h', 'Aciclovir,  aminofilina,  anfotericina B, anfotericin B liposomal,  ampicilina, ampicilina/sulbactam, dexametasona sódica, dobutamina, furosemida, ganciclovir, indometacina, omeprazol,  fenobarbital,fenitoína, piperacilina/tazobactam, bicarbonato sódico, tiopental, nutrición parenteral.', '- Estabilidad por 24 horas ya diluido.', '-Vigilar depresión respiratoria y deterioro hemodinámico\r\n-Monitorizar escala de sedación \r\n-Valorar agitación psico-motora\r\n-Antídoto ante signos de intoxicación: flumazenil 0,01 mg/kg/dosis máximo 0,2 mg por dosis. Repetir cada minuto hasta revertir sobredosificación o alcanzar dosis máxima acumulada de 1 mg/kg', 'dhernandez', '2024-05-27 22:55:18', 'jramos', 1, 'Bolo: Diluir en 3 a 5 ml\r\nInfusión continua: PURO\r\nConcentración: 1.000 mcg/ml', 'NA'),
(16, 'FENOBARBITAL', 13, 24, 'Ampolla 200 mg/ml', 'Segunda línea en el tratamiento de crisis convulsivas generalizadas tipo gran mal, en crisis parciales y en el estatus epiléptico tras benzodiacepinas y fenitoína (vía parenteral).\r\n\r\nPrimera línea en el tratamiento de convulsiones neonatales y en &amp;lt;1 año.', 'Dosis de carga: 15-20 mg/kg/día dosis única\r\nDosis de mantenimiento: 4 a 8 mg/kg/día dividido en 1 o 2 dosis', 'N.A', 'Concentración máxima permitida por ml de la mezcla para infundir: 10 mg/ml \r\n\r\nDiluir en SSN 0.9%', 'Administrar en 30 a 60 minutos tanto el bolo como las dosis de mantenimiento', 'Insulina, metadona, pancuronio, ranitidina y vancomicina', 'Si no se usa inmediatamente, la solución reconstituida se puede almacenar y debe ser protegida de la luz a una temperatura ambiente de entre 20 y 25 grados C (68 y 77 grados F) durante un máximo de 8 horas o en el refrigerador entre 2 y 8 grados. C (36 y 46 grados F) durante un máximo de 24 horas. \r\n\r\nDeseche cualquier porción no utilizada después del período de almacenamiento recomendado.', 'Monitorización cardiovascular y neurológica durante la administración \r\nVigilar: náuseas y constipación', 'dhernandez', '2024-05-27 23:42:12', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\nEjemplo: \r\nNiño 15 kg\r\n60 mg / 10 (mg/ml) = 6 ml (volumen de dilución mínimo)\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(18, 'FENTANILO CITRATO', 13, 25, 'Ampolla 500 mcg / 10 ml', 'Como analgésico narcótico complementario en anestesia general o local.', 'Bolo: 1 a 3 mcg/kg/dosis\r\nInfusión continua: 0.5 a 5 mcg/kg/hora', 'N.A', 'Concentración máxima permitida por ml de la mezcla para infundir: 50 mcg/ml\r\n\r\nDiluir con SSN 0,9%', 'Bolo: 3 a 5 minutos.\r\nInfusión continua: según dosis deseada\r\nml/h= dosis en mcg x peso en kg / mcg del medicamento por ml\r\nejemplo: Usuario 15 kg dosis prescrita: 0.5 mcg/kg/h\r\nml/h= 2 x 15/50  = 0,6 ml/h', 'Anfotericina B, fenitoina sodica, amiodarona, ampicilina, ampicilina sulbactam y azitromicina', 'Cambiar la mezcla cada 24 horas', 'Monitoreo continuo, puede inducir hipotensión, bradicardia, depresión respiratoria,\r\nsedación profunda y disminución importante de la motilidad intestinal. Contraindicado\r\nen hipertensión endocraneana por disminución de la perfusión cerebral, e insuficiencia\r\nhepática y renal grave\r\n- Administrar bolo lento (3 a 5 min) para disminuir riesgo de tórax en leño (antídoto: naloxona 0.01 mg/kg)', 'dhernandez', '2024-05-27 23:47:17', 'jramos', 1, 'Bolo: Diluir en 5 ml\r\nInfusión continua: \r\n&lt; 10 kg: 10 ml (500 mcg) +40 ml de SSN\r\nConcentración: 10 mcg/ml\r\n&gt;10 kg: PURO \r\nConcentración: 50 mcg/ml', 'NA'),
(19, 'MORFINA CLORHIDRATO', 13, 25, 'Ampolla 10 mg / 1 ml', 'Analgesia y sedación', 'Bolo: 0,05 - 0,1 mg/kg/dosis. Máximo 15 mg/24 h.\r\nInfusión continua: 5 a 20 mcg/kg/h', 'N.A', 'Concentración máxima permitida por ml de la mezcla para infundir: 1 mg/ml', 'Bolo de 10 a 30 minutos\r\nInfusión continua: según dosis deseada\r\nml/h= dosis en mcg x peso en kg / mcg del medicamento por ml\r\nejemplo: Usuario 15 kg dosis prescrita: 5 mcg/kg/h\r\nml/h= 5 x 15 / 100 = 0.7 ml/h', 'Aciclovir, Anfotericina B, Anfotericina B liposomal, ganciclovir, indometacin, fenitoina, furosemida,  insulina, ampicilina/sulbactam.', '- Una vez abierta la ampolla tiene una vida media de dos horas.\r\n\r\n- La luz no altera la estabilidad del medicamento en 24-48-72 horas, ni la temperatura entre 20-25 grados', '-Control de signos vitales, puede producir hipotensión (mareos), bradicardia y depresión respiratoria\r\n-Puede producir retención urinaria\r\n-Vigilar efectos gastrointestinales a largo plazo como estreñimiento', 'dhernandez', '2024-05-27 23:52:10', 'jramos', 1, 'Bolo: Diluir en 5 ml\r\nInfusión continua:\r\n&lt; 10 kg: 0.3 ml (3 mg) + 49.7 de SSN\r\nConcentración: 60 mcg/ml\r\n&gt; 10 kg: 0.5 ml (5 mg) + 49.5 de SSN\r\nConcentración: 100 mcg/ml', 'NA'),
(20, 'FITOMENADIONA', 13, 26, 'Ampolla 10 mg / 1 ml', 'Hemorragias o peligro de hemorragias como resultado de hipoprotrombinemia severa debida a deficiencia de factores de la coagulación II, V, VII y X.', 'Dependiente del INR en un rango de 0,2 a 2 mg/kg/día', 'N.A', 'Concentración máxima permitida por ml de la mezcla para infundir: 1 mg/ml\r\n\r\nDiluir en SSN 0,9% o Dextrosa al 5%', '30 a 60 minutos', 'No debe diluirse o mezclarse con otros inyectables', '- Consérvese a tempertura ambiente a no más de 25°C.\r\n\r\n- Protéjase de la luz.', 'Vigilar hipotensión, mareo y molestias gastrointestinales\r\nVigilar sitio de inserción: dolor y sensibilidad (aumentar el volumen de dilución si procede)', 'dhernandez', '2024-05-28 00:03:21', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\nEjemplo: \r\nNiño 15 kg\r\n3 mg / 2(mg/ml) = 3 ml (volumen de dilución mínimo)\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(21, 'FUROSEMIDA', 13, 27, 'Ampolla 20 mg / 2 ml', 'Diurético\r\nSobrecarga de volumen', '1 a 2 mg/kg/dosis cada 8 a 6 horas\r\nInfusión continua: 0,05 - 0.5 mg/kg/hora', 'N.A', 'Concentración máxima permitida por ml de la mezcla para infundir: 10 mg/ml\r\n\r\nDiluir en SSN 0,9% o Dextrosa 5%', 'Bolo o mantenimiento: 15 a 30 minutos\r\nInfusión continua: según dosis deseada\r\nml/h= dosis en mg x peso en kg / mg del medicamento por ml\r\nejemplo: Usuario 15 kg dosis prescrita: 0.05 mg/kg/h\r\nml/h= 0.05x15/0.8 = 0.9 ml/h', 'Potencia la ototoxicidad de: aminoglucósidos, cisplatino y otros fármacos ototóxicos.\r\n\r\nPotencia la nefrotoxicidad de antibióticos nefrotóxicos, cisplatino y ciclosporina.', '- Usar equipo fotosensible para administración de infusión continua.\r\n- Almacenar a temperatura entre 15 - 30°c (25°c) (fotosensible).\r\n- Estabilidad: 24h.', '- Vigilar efectos como temblores, dolor de cabeza, evaluar constantemente la tensión arterial, urticaria, prurito, realizar evaluaciones sanguíneas y glucometrías constantes. \r\n\r\n- Vigilar ototoxicidad y daño renal. \r\n\r\n- Vigilar niveles séricos de potasio. Produce hipocalemia', 'dhernandez', '2024-05-28 00:07:07', 'jramos', 1, 'Bolo o mantenimiento: diluir en 5 a 10 ml.\r\nInfusión continua: \r\n&lt;10 kg: 1 ml de furosemida (10 mg) + 49 ml SSN\r\nConcentración: 0.2 mg/ml\r\n&gt;10 kg: 4 ml de furosemida (40 mg) + 46 ml SSN\r\nConcentración: 0.8 mg/ml', 'Bolo o mantenimiento: 10 ml\r\nInfusión continua: 50 ml'),
(22, 'KETAMINA CLORHIDRATO', 13, 28, 'Ampolla 500 mg / 10 ml', 'Anestésico y sedante.', 'Bolo: 0.5 a 1 mg/kg\r\nInfusión continua: 5 a 20 mcg/kg/min', 'N.A', 'Concentración máxima permitida por ml de la mezcla para infundir: 1 mg/ml\r\nDiluir en SSN 0,9% o DAD 5%', 'Bolo: 1 minuto\r\nInfusión continua: según dosis deseada\r\nml/h= dosis en mcg x peso en kg x 60 min / mcg del medicamento por ml\r\nejemplo: Usuario 15 kg dosis prescrita: 5 mcg/kg/min\r\nml/h= 5x15x60/5.000 = 0.9 ml/h', 'Incompatible para paso en Y con: Barbitúricos, agonistas opiáceos, benzodiacepinas, hormonas tiroideas, antihipertensivos, aminofilina', 'Estable diluido por 48 horas se debe conservar refrigerado (2-8 °C)\r\nCambiar mezcla mínimo cada 24 horas', 'Monitoreo hemodinámico completo y permanente\r\nMonitoreo neurológico', 'dhernandez', '2024-05-28 00:12:12', 'jramos', 1, 'Bolo: diluir dosis en 5 ml y pasar en IV directo en 1 minuto\r\nInfusión continua:  \r\n&lt; 10 kg: 2.5 ml (125 mg) + 47.5 ml de SSN \r\nConcentración: 2.500 mcg/ml\r\n&gt; 10 kg: 5 ml (250 mg) + 45 ml de SSN \r\nConcentración: 5.000 mcg/ml', 'NA'),
(23, 'LIDOCAINA', 13, 28, 'Ampolla 10 ml al 2 % sin epinefrina  (1 cc=20 mg = 20.000 mcg)', 'Taquicardia ventricular', 'Bolo: 1 mg/kg/dosis única\r\nInfusión continua: 20 a 50 mcg/kg/min', 'N.A', 'Concentración máxima permitida por ml de la mezcla para infundir: 8.000 mcg/ml', 'Bolo: 20 a 30 minutos\r\nInfusión continua: según dosis deseada\r\nml/h= dosis en mcg x peso en kg x 60 min / mcg del medicamento por ml\r\nejemplo: Usuario 15 kg dosis prescrita: 20 mcg/kg/min\r\nml/h= 20x15x60/4.000 = 4.5 ml/h', 'Anfotericina B', '- Estabilidad por 24 horas.\r\n\r\n- Vigilar fenomeno de sorción con el pvc después de las 72 horas.', 'Monitoreo hemodinámico permanente', 'dhernandez', '2024-05-28 00:17:42', 'jramos', 1, 'Bolo: Diluir en 30 a 50 ml\r\nInfusión continua: 10 ml de Lidocaína  al 2% sin epinefrina (200 mg) + 40 ml de SSN\r\nConcentración: 4.000 mcg/ml', 'NA'),
(24, 'OMEPRAZOL', 13, 29, 'Polvo liofilizado para solución inyectable 40 mg', 'Reflujo gastroesofágico y gastritis', '10-20 mg/kg/día dividido en 2 dosis', 'Se reconstituye en 4 mL de SSN.', 'Concentración máxima permitida por ml de la mezcla para infundir: 4 mg/ml', '20 a 30 minutos', 'Atazanavir, micofenolato, rilpivirina, metotrexato. Ondasetron', 'Estabilidad química y física de hasta 12 h a 25 °C tras la reconstitución', 'Vigilar los efectos secundarios mas comunes:cefalea, diarrea, estreñimiento y náuseas.', 'dhernandez', '2024-05-28 00:20:51', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n75 mg / 4(mg/ml) = 18 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(25, 'ONDANSETRÓN', 13, 30, 'Ampolla 8 mg / 4 ml', 'Antagonista potente y altamente selectivo de los receptores 5HT3.  Control de nauseas y/o vomitos.', '0,15 mg/kg/dosis \r\n\r\nDosis máxima: 8 mg', 'N.A', 'Concentración máxima permitida por ml de la mezcla para infundir: 2 mg/ml', '20 a 30 minutos.', 'N.A', 'Estabilidad durante 48 horas a 25 °C.', 'Vigilar efectos secundarios gastrointestinales, a nivel del sistema nervioso y flebitis.', 'dhernandez', '2024-05-28 00:24:33', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n2.25 mg / 2(mg/ml) = 2 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(26, 'CAFEINA', 13, 31, 'Ampolla 20 mg/ml', 'La mayoría de sus efectos se ha atribuido al antagonismo de los receptores de adenosina, y su acción principal la estimulación del sistema nervioso central (SNC). Esta es la base de su efecto en la apnea del prematuro', 'Dosis carga:  20-25 mg/kg\r\nDosis mantenimiento: 5-10 mg/kg', 'N.A', 'Concentración máxima permitida por ml de la mezcla para infundir: 20 mg/ml', 'Dosis de carga: 30 minutos\r\nDosis de mantenimiento: 10 minutos', 'N.A', 'La solución diluida es estable durante 24 h a 25 °C y a 2-8 °C.', '- Vigilar si presenta aumento de la diuresis, vigilar signos de hipoglucemia, irritabilidad y vómitos \r\n\r\n- Vigilar flebitis.', 'dhernandez', '2024-05-28 00:27:52', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n75 mg / 20 (mg/ml) = 4 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(27, 'AMIKACINA', 12, 1, 'Ampolla 100mg/2ml\r\nAmpolla 500mg/2ml', 'N.A.', '15-22 mg/kg/día divididos en 3 dosis', 'N.A.', 'Concentración máxima permitida por ml de la mezcla para infundir: 10 mg/ml', '1 hora', 'Anfotericina B,ampicilina, azitromicina, heparina (concentraciones superiores a 1 unidad / ml), imipenem / cilastatina, mezlocilina, nafcilina, oxacilina, fenitoína, propofol, tiopental y ticarcilina / clavulánico', 'Estabilidad: 24h diluido.', 'Vigilar signos de nefrotoxicidad: pruebas de función renal, diuresis\r\nVigilar ototoxicidad: Tinitus, pérdida o disminución de la audición \r\nComprobar diuresis y cuantificar gasto urinario', 'jdelgado', '2024-05-28 01:54:10', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n75 mg / 10 (mg/ml) = 7,5 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(28, 'GENTAMICINA SULFATO', 12, 1, 'Ampolla 40 mg/ml o 80 mg / 2 ml', 'Antibioterapia', '6-7,5 mg/kg/día en 1 o 2 dosis', 'N.A.', 'Concentración máxima permitida por ml de la mezcla para infundir: 10 mg/mL', '1 hora', 'Anfotericina B, ampicilina, azitromicina, furosemida, heparina, oxacilina Y propofol', 'Estable por 24 horas diluido.', '-Vigilar toxicidad renal: Pruebas de función renal, diuresis\r\nToxicidad neurológica:  ataxia, mareo, fiebre, marcha inestable, cefalea, vértigo\r\nToxicidad ótica: tínitus, pérdida de la audición.\r\n-Cuantificar gasto urinario', 'jdelgado', '2024-05-28 02:12:53', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n90 mg / 10 (mg/ml) = 9 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(29, 'AMPICILINA SODICA', 12, 2, 'Polvo liofilizado para solución inyectable 1gr', 'Antibioterapia', 'Infección leve-moderada: Dosis permitida: 100-200 mg/kg/día, cada 6 horas. Máximo 4 g / día\r\n\r\nInfección severa: Dosis permitida: 200 - 400 mg/kg/día, cada 6 horas. Máximo 12 gr / día', 'Reconstituir en 10 ml de SSN 0,9% para una concentración de 100 mg / ml', 'Concentración máxima permitida: 30 mg/ml en SSN 0,9%', 'Bolo IV dosis &lt; 500 mg administrar de 3 - 5 minutos\r\n\r\nBolo IV dosis &gt; 500mg adm de 10 - 15 minutos\r\n\r\nInfusión intermitente administrar dosis en 1 hora.', 'Aminofilina, anfotericina B liposomal, atropina, aminoglucosidos(amikacina, gentamicina), gluconato de calcio, cefazolina, dexametasona, fluconazol, furosemida, heparina, gentamicina, hidrocortisona, insulina, fenobarbital, vancomicina.', 'Estable por 1 hora diluido, administrar de inmediato.', 'Vigilar sintomas gastrointestinales, funcion renal y realizar examenes hematologicos si es necesario. Vigilar flebitis.', 'jdelgado', '2024-05-28 02:50:21', NULL, 1, '', ''),
(30, 'AMPICILINA SULBACTAM', 12, 2, 'Polvo liofilizado para solución inyectable 1.5 gr (1 gr ampicilina más 0.5 gr de sulbactam)', 'Antibiótico', 'Infección leve-moderada: 100 mg/kg/día dividido en 4 dosis\r\nInfección severa: 100-200 mg/kg/día dividido en 4 dosis', 'Reconstituir en 10 ml de de AD o SSN 0,9%', 'Concentración máxima permitida por ml de la mezcla para infundir: 45 mg/ml', 'Administrar en 1 hora.', 'Aminofilina, amiodarona, anfotericina B liposomal, ampicilina, atropina, gluconato de calcio, cefazolina, dexametasona, fluconazol, furosemida, heparina, hidrocortisona, insulina, metronidazol, morfina, fenobarbital, bicarbonato sodio, vancomicina.', 'Estable por 2 horas diluido.', 'Vigilar reacciones alérgicas, síntomas gastrointestinales y función renal', 'jdelgado', '2024-05-28 02:54:22', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n375 mg / 45 (mg/ml) = 8.3 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(31, 'AZTREONAM', 12, 3, 'Polvo liofilizado para solución inyectable 1 gr', 'Antibiótico', '150-200 mg/kg/día dividido en 3 a 4  dosis\r\nMáximo: 2 g/dosis', 'Reconstituir en 10 ml de de SSN 0,9% para una concentración de 150 mg / ml', 'Concentración máxima permitida por ml de la mezcla para infundir: 20 mg/ml', 'Administrar en 1 hora.', 'Puede disminuir efecto de la vacuna BCG y antitifoidea, no usar con betalactamicos y antocouagulantes.', 'Estabilidad: Hasta 48 h tras su preparación a temperatura ambiente (15-30 °C) o 7 días en nevera (2-8 °C).', 'Vigilar emesis, diarrea, rash, función hematológica, hepática y renal. Vigilar fiebre y signos de flebitis.', 'jdelgado', '2024-05-28 02:58:08', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n750 mg / 20(mg/ml) = 37,5 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(32, 'CEFAZOLINA LASA', 12, 4, 'Polvo liofilizado para solución inyectable 1 gr', 'Antibiótico', 'Infección moderada: Dosis permitida: 50 - 75 mg/kg/día cada 6 horas Máximo 1.000 mg/dosis.\r\n\r\nInfección severa: Dosis permitida: 100 - 150 mg/kg/día cada 6 horas Máximo 2.000 mg/dosis', 'Reconstituir en 10 ml de SSN 0,9% para una concentración de 100 mg / ml', 'Concentración máxima permitda en bolo: 100 mg/ml                                    Concentracion maxima permitida en infusión: 20 mg/ml en DAD 5% o SSN 0,9%', 'Bolo: administrar dosis de 3 a 5 minutos\r\n\r\nInfusión intermitente: administrar dosis de 10 a 60 minutos', 'Ampicilina, metronidazol, penicilina sulbactam, vancomicina.', 'Estabilidad: 12h (temperatura ambiente) / refrigerado 10 días.         No sufre adherencia al PVC  al usar por 1 semana el medicamento a temperatura ambiente', 'Vigilar sintomas gastrointestinales, funcion renal y realizar examenes hematologicos si es necesario. Vigilar signos de tromboflebitis.', 'jdelgado', '2024-05-28 03:01:57', NULL, 1, '', ''),
(33, 'CEFRADINA LASA', 12, 4, 'Polvo liofilizado para solución inyectable 1 gr', 'Antibiótico', '50-100 mg/Kg/día dividido en 4 dosis', 'Reconstituir en 10 mL de SSN 0,9% concentracion de 100 mg/ml', 'Concentración máxima permitida por ml de la mezcla para infundir: 50 mg/mL', 'Administrar en 1 hora.', 'Diureticos de asa, warfarina, probenecid.', 'Al diluirse tiene una estabilidad de 24 horas en agua esteril y en SSN estabilidad de hasta 7 dias en refrigeracion de 2-8°c.', 'Vigilar reacciones gastrointestinales, reaccion dermica como exantema y funcion hematologica. Vigilar flebitis en sitio y otras reacciones como la fiebre.', 'jdelgado', '2024-05-28 03:09:42', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n187mg / 50 (mg/ml) = 4 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(34, 'CEFEPIME', 12, 5, 'Polvo liofilizado para solución inyectable 1 gr', 'Antibiótico', '50 mg/kg/día dividido en 3 dosis\r\nMáximo 2.000 mg/dosis', 'Reconstituir en 10 ml de solución salina 0,9% para una concentración de 100 mg / ml', 'Concentración máxima permitida por ml de la mezcla para infundir: 40 mg/ml', '1 hora.', 'Gentamicina, amikacina, aminofilina, metronidazol, vancomicina.', 'Estabilidad: temperatura ambiente durante 24 horas entre 20°c y 25°c, refrigerada 7 días entre 2°c y 8°c', 'Vigilar signos neurologicos (Cefalea), vigilar presencia de prurito o rash, vigilar emesis o reacciones gastrointestinales y función renal.', 'jdelgado', '2024-05-28 03:18:46', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n250 mg / 40 (mg/ml) =  6,5 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(35, 'CEFOTAXIMA', 12, 6, 'Polvo liofilizado para solución inyectable 1 gr', 'Antibiótico', '150 a 180 mg/kg/día dividida en 3 dosis. \r\nDosis máxima: 8 g/día', 'Reconstituir en 10 ml de AD o SSN a 0,9%', 'Concentración máxima permitida por ml de la mezcla para infundir: 50 mg/ml', '15 a 30 minutos', 'La cefotaxima puede aumentar los niveles/efectos toxicos para el riñon en los medicamentos aminoglucósidos o diureticos y no combinar con agentes bacteriostaticos.', 'Estabilidad: despues de diluir la mezcla reconstituida es estable de 12 a 24 hora a temperatura ambiente y 7 dias en  refrigeración (2°C-8°C) medicamento fotosensible.', 'Vigilar reacciones como: Fiebre, arritmias, cefalea, convulsiones, rash o prurito, reaccion gastrointestinal como vomitos, funcion renal, hematologica, hepatica y flebitis.', 'jdelgado', '2024-05-28 03:22:02', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n750 mg / 50 (mg/ml) = 15 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)');
INSERT INTO `medpedintravenoso` (`id`, `nombre`, `subcategoria`, `agrupamiento`, `presentacion`, `uso`, `dosis`, `reconstitucion`, `condilucion`, `tieinfusion`, `incsitio`, `cuipreparacion`, `cuiadministracion`, `usuario1`, `fechareg`, `usuario2`, `estado`, `dilucionmin`, `dilucionmax`) VALUES
(36, 'CEFTAZIDIME + AVIBACTAM', 12, 6, 'Polvo liofilizado para solucion inyectable 2,5 gr', 'Antibiótico', '100 a 150 mg/kg/día dividido en 3 dosis.', 'Reconstituir en 10 ml de SSN 0,9% para una concentración de 100 mg / ml', 'Concentración máxima permitida por ml de la mezcla para infundir: 40 mg/ml', 'Administrar en 2 horas.', '&amp;quot;Probenecid podría incrementar la concentración de ceftazidima y alterar la eliminación de avibactam. No se recomienda su administración conjunta.\r\nCloranfenicol: antagonista in vitro de la ceftazidima. No se recomienda su administración conjunta.\r\nAminoglucósidos: aumento del riesgo de nefrotoxicidad.\r\nCefalosporinas a altas dosis: aumento del riesgo de nefrotoxicidad.', 'Estabilidad corta se recomienda usar inmediatamente diluido el medicamento.', 'Vigilar funcion hepatica y renal y otras reacciones como vomito, rash y flebitis.', 'jdelgado', '2024-05-28 03:24:57', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n500 mg / 40 (mg/ml) = 12,5 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(37, 'CEFTRIAXONA', 12, 6, 'Polvo liofilizado para solucion inyectable 1 gr', 'Antibiótico', 'Infección moderada: 50 a 75 mg/kg/día dividido en 2 dosis\r\nInfección severa:  100 mg/kg/día dividido en 2 dosis\r\nMáximo 4.000 mg/día', 'Reconstituir en 10 ml de AD o SSN 0,9%', 'Concentración máxima permitida por ml de la mezcla para infundir: 40 mg/ml', 'Bolo: Administrar dosis de 2 - 4 minutos (en niños mayores de 11 años)\r\n\r\nInfusión intermitente: Administrar dosis minimo en 15 - 30 minutos', 'Soluciones o productos que contienen Calcio (incluso Lactato de Ringer o nutrición parenteral).  Amsacrina, Vancomicina, Fluconazol y Aminoglucósidos.', 'Estabilidad: temperatura ambiente 6 horas - temperatura 5°c 24 horas', 'Vigilar reacciones gastrointestinales, reaccion dermica como exantema y funcion hematologica. Vigilar flebitis en sitio.', 'jdelgado', '2024-05-28 03:27:36', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n375 mg / 40 (mg/ml) = 9,3 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(38, 'CLARITROMICINA MEDICAMENTO LASA', 12, 7, 'Ampolla x 500 mg', 'Antibiótico', '15 mg/kg/día dividido en 2 dosis\r\nDosis máxima 1g día.', 'Diluir con su propio diluyente con 10 ml de agua estéril ÚNICAMENTE', 'Concentración máxima permitida por ml de la mezcla para infundir: 2mg/mL', '60 minutos.', 'Por precaucion administrar solo este medicamento por reaccion de incompatibilidad', 'Estabilidad a temperatura ambiente de 24 horas y en refrigeración 48 horas.', 'Realizar examenes de sangre, vigilar estado mental del paciente, vigilar reacciones como convulsiones, vigilar funcion gastrointestinal y funcion renal.', 'jdelgado', '2024-05-28 03:30:07', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n112 mg / 2(mg/ml) = 56 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)\r\n\r\nNota: Para la claritromicina la recomendación de la concentración máxima permitida por ml supera el valor de dilución máxima. En este caso se debe seguir la recomendación de [ ] por ml.'),
(39, 'CLINDAMICINA', 12, 8, 'Ampolla 600 mg / 4 ml', 'Antibiótico', 'Infecciones leves a moderadas: 20 mg/kg/día dividido en 3 a 4 dosis\r\nInfecciones graves:  40 mg/kg/día dividido en 3 a 4 \r\nDosis máxima diaria: 2700 mg/día', 'N.A.', 'Concentración máxima permitida por ml de la mezcla para infundir: 18 mg/ml', 'Administrar dosis en 1 hora.', 'Eritromicina - Mecamilamina', 'Estabilidad a temperatura ambiente de 24 horas y en refrigeración 48 horas.', 'Vigilar la presencia de flebitis y erupción cutánea. Pude presentarse sintomas gastrointestinales y elevacion de enzimas hepaticas, dolor de cabeza y mareo.', 'jdelgado', '2024-05-28 03:32:24', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n100 mg / 18 (mg/ml) = 5.5 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(40, 'FLUCONAZOL', 12, 9, 'Ampolla 200 mg / 100 ml', 'Antibiótico', '6-12 mg/Kg/dia.', 'N.A.', 'NA', 'Administrar en 60 minutos', 'Anfotericina B, ampicilina, gluconato de \r\ncalcio, cefotaxima, ceftazidima, \r\nceftriaxona, cloranfenicol, clindamicina, digoxina, \r\nfurosemida, imipenem, piperacilina y \r\ntrimetoprim sulfa.', 'Mantener a  temperatura \r\nambiente, no refrigerar ni congelar.', 'Hepatotoxicidad y produce daño renal, vigilar signos de sobreinfeccion y flebitis.', 'jdelgado', '2024-05-28 03:35:09', 'jramos', 1, 'NA', 'NA'),
(41, 'ANIDULAFUNGINA', 12, 9, 'Vial 100 mg', 'Antibiótico', '3 mg/kg/día el primer día (dosis máxima: 200 mg) y continuar con 1,5 mg/kg/día (dosis máxima: 100 mg).', 'reconstituir con su propio diluyente', 'Concentración máxima permitida por ml de la mezcla para infundir: 0,77 mg/ml', 'Velocidad de perfusión: 1 mg/min', 'Ciclosporina, tacrolimus, rifampicina, anfotericina B y voriconazol.', 'La solución reconstituida puede conservarse a temperatura de hasta 25 °C durante 24 horas. La solución diluida para perfusión puede conservarse a 25 °C durante 48 horas o conservarse congelada durante al menos 72 horas.', 'Vigilar hipopotasemia, diarrea y náuseas, hiperglucemia, convulsiones, cefalea, hipotensión e hipertensión,', 'jdelgado', '2024-05-28 03:58:22', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n22.5 mg / 0.77 (mg/ml) = 29.2 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(42, 'CASPOFUNGINA', 12, 9, 'Vial liofilizado ampolla  x 50 mg \r\n\r\nVial liofilizado ampolla  x 70 mg', 'Antibiótico', '70 mg/m2 el primer día (máximo 70 mg) \r\n\r\nDosis de mantenimiento: 50 mg/m2/día (máximo 70 mg al día).', 'Reconstituir el vial con 10.8 ml de SSN 0.9% 10.8 ml AD', 'Concentración máxima permitida por ml de la mezcla para infundir: 0,5 mg/ml', '90 minutos', '- Anfotericina B o azoles puede ser sinérgica (incluso frente a hongos resistentes como Fusarium y Zygomicetes) o aditiva.\r\n\r\n- Algunos fármacos inductores enzimáticos aumentan el aclaramiento de caspofungina disminuyendo por tanto sus niveles plasmáticos (carbamazepina, dexametasona, efavirenz, nevirapina, fenitoína, rifampicina). \r\n\r\n- Las ciclosporina aumenta el riesgo de elevación de transaminasas.\r\n\r\n- Los tacrolimus puede reducir la concentración plasmática de este.', '- La solución reconstituida se puede conservar hasta 24 horas a una temperatura igual o inferior a 25°C. \r\n\r\n- La solución para perfusión se debe utilizar en un plazo de 24 horas si se conserva a una temperatura igual o inferior a 25°C o en un plazo de 48 horas si se conserva refrigerada entre 2 y 8°C', '- Vigilar pirexia, exantema y dolor de cabeza, taquicardia, hipotensión, elevación enzimas hepática, diarrea, prurito, rash, y reacciones locales en el lugar de administración (fiebre, escalofríos, irritación y flebitis).\r\n\r\n- También se han notificado alteraciones electrolíticas (hipomagnesemia, hipopotasemia, hiperglucemia, hipo e hiperfosfatemia).', 'dhernandez', '2024-05-28 12:35:47', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n26 mg / 0.5 (mg/ml) = 52, 5 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(43, 'MEROPENEM', 12, 10, 'Vial 1 gr', 'Antibiótico', '10 a 20 mg/kg/dosis cada 8 horas \r\nMeningitis: 40 mg/kg/dosis cada 8 horas', 'Reconstituir con 10 mL por cada 500 mg de SSN o agua estéril.', 'Concentración máxima permitida por ml de la mezcla para infundir:  20 mg/ml', '30 minutos.', 'Aciclovir, anfotericina B, gluconato de calcio, metronidazol, bicarbonato de sodio y zidovudina.', 'Estabilidad según la concentración de la mezcla: 20 mg / mL: 1h a 2 temperatura ambiente o 15 horas en refrigeración. 10 mg / ml: hasta 12 h a temperatura ambiente.', 'Vigilar posibles efectos secundarios como: Hipotensión, Bradicardia, extravasación en venas periféricas', 'dhernandez', '2024-05-28 12:46:01', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n150 mg / 20(mg/ml) = 7,5 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(44, 'METRONIDAZOL LASA', 12, 11, 'Ampolla 500 mg / 100 ml', 'Antibiótico', '30 a 40 mg/kg/día dividido en 3 dosis\r\nDosis Máxima: 500 mg/dosis.', 'N.A', 'N.A', '1 hora', 'Meropenem, Fenobarbital, Rifampicina, Fenitoína.', 'Estable por 96 horas en refrigeración.', 'Vigilar efectos secundarios gastrointestinales y vigilar función hepática', 'dhernandez', '2024-05-28 12:51:07', 'jramos', 1, 'NA', 'NA'),
(45, 'PENICILINA G CRISTALINA', 12, 12, 'Polvo liofilizado para solución inyectable 5.000.000 UI', 'Antibiótico', '100.000 - 400.000 UI/kg/día divididas cada 4 a 6 horas', 'Reconstituir en 10 ml en AD', 'Concentración máxima permitida por ml de la mezcla para infundir: 100.000 UI/ ml', 'Infusión intermitente: administrar dosis en 30 minutos.', 'Anfotericina B, metoclopramida, fenitoína, amikacina, gentamicina, fenobarbital, trimetropim sulfametozasol.', 'Estabilidad entre 2°C a 8°C por 72 horas, ambiente 12 horas.', '- Vigilar electrolitos, convulsión , función hepática. \r\n\r\n- Observar signos de hipersensibilidad. \r\n\r\n- Puede provocar flebitis', 'dhernandez', '2024-05-28 13:56:48', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n375.000 UI / 100.000(UI/ml) = 3,75 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(46, 'PIPERACILINA TAZOBACTAM', 12, 13, 'Solución inyectable 4gr de piperacilina y 0.5 gramos de tazobactam', 'Antibiótico', 'Lactantes de 2 a 9 meses: 200-300 mg/kg/día dividido en 4 dosis.\r\n\r\nLactantes &amp;gt; 9 meses, Niños y Adolescentes: 100 mg/kg/dosis cada 8 horas; Dosis máxima diaria: 16 gr/día', 'Reconstituir en 20 ml de agua estéril o solución salina', 'Concentración máxima permitida por ml de la mezcla para infundir: 40 mg/ml \r\nNo debe diluirse en lactato de ringer', '2 horas', 'Aciclovir, gentamicina, insulina, vancomicina', 'Diluida es estable por 24 horas.', '- Vigilar reacciones alérgicas\r\n-Vigilar electrolitos, función renal, hepática y hematopoyética periódica en tratamiento prolongado. \r\n- Produce flebitis. Si no está contraindicado, usar el volumen máximo de dilución (Holliday-Segar)', 'dhernandez', '2024-05-28 14:00:46', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n1500 mg / 40(mg/ml) = 37,5 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'Dilucion máxima\r\nLa dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(47, 'POLIMIXINA B', 12, 14, 'Cada frasco contiene 500.000 UI de Polimixina B', 'Antibiótico', 'Niños &lt; 2 años: 25.000 unidades/kg/día dividido en 4 dosis\r\nNiños &gt; 2 años:  40.000 unidades/kg/día dividido en cuatro dosis', 'N.A', 'Concentración máxima permitida por ml de la mezcla para infundir: 1500 UI/ml', '60 a 90 minutos', 'Disminuye el efecto de las vacunas Calmette-Guérin y fiebre tifoidea. No usar con relajantes neuromusculares y evitar usar con medicamentos nefrotóxicos.', 'Estabilidad en refrigeración hasta de 72 horas', 'Vigilar función renal, trastornos del sistema nervioso central y flebitis.', 'dhernandez', '2024-05-28 14:03:25', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n94.000 UI / 1500 (UI/ml) = 62 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(48, 'COLISTIMETATO DE SODIO', 12, 14, 'VIAL 150 mg', 'Antibiótico', 'Colistina base: 2.5 - 5 mg/kg/día dividido en 2 a 4 dosis', 'Reconstituir con 10 ml de AD o SSN', 'Concentración máxima permitida: 1 mg/ml', '30 minutos', 'Relajantes neuromusculares, otros antibióticos con efectos nefrotóxico (vancomicina, aminoglucósidos, anfotericina B) debe evitarse o realizarse con la máxima precaución, debido a que pueden potenciarse los efectos neuro y nefrotóxicos. evitar uso conjunto con eritromicina, tetraciclina o cefalotina (riesgo de precipitación).', 'La solución diluida puede utilizarse entre 24 horas a 2-8 °C u 8 horas a temperatura inferior a 25 °C.', 'Vigilar mareos, debilidad, parestesia facial y periférica, vértigo, alteraciones visuales, confusión, ataxia y bloqueo neuromuscular, que puede conducir a una insuficiencia respiratoria o apnea.', 'dhernandez', '2024-05-28 14:10:59', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n18 mg / 1(mg/ml) = 18 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(49, 'VANCOMICINA', 12, 15, 'Polvo liofilizado para solución inyectable 500 mg', 'Antibiótico', '40 a 60 mg/kg/día divididas en tres dosis', 'Reconstituir con 10 ml de Solución salina 0,9% o Dextrosa 5%', 'Concentración máxima permitida por ml de la mezcla para infundir: 5 mg', '2 horas', 'Cefazolina, cefepima, cefotaxima, ceftriaxona, cloranfenicol, dexametasona, fenobarbital, piperacilina, piperacilina/tazobactam, anfotericina B liposomal, ampicilina, furosemida, heparina, hidrocortisona, fenitoína', 'Vial reconstituido 14 días a T° 2 - 8 °C / Solución diluida 96 H a T° 2 - 8 °C', '- Síndrome de hombre rojo: RASH e hipotensión.\r\n- Vigilar signos de infiltración y flebitis.\r\n- Monitorizar diuresis y función renal', 'dhernandez', '2024-05-28 14:14:29', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n200 mg / 5(mg/ml) = 40 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(50, 'ACICLOVIR', 12, 16, 'Ampolla 250 mg / 10 ml', 'Antiviral', '20 mg - 60 mg/kg/día dividida en 4 dosis.', 'No aplica', 'Concentración máxima permitida: 7 mg/ml \r\n\r\nDiluir con Dextrosa 5% o SSN 0.9%', 'Infusión lenta: Administrar dosis tiempo 60 minutos', 'Acetaminofén, amiodarona, anfotericina B, Ampicilina-sulbactam, caspofungina, cefepima, ciprofloxacina,dobutamina, dopamina, gentamicina, ketamina, levofloxacina, lidocaína, meropenem, metoclopramida, morfina, nitroprusiato de sodio ondansetrón, piperacilina-tazobactam, sargramostim, vecuronio.', 'Vigilar diarrea, vómitos, picazón y erupciones en la piel. Vigilar pruebas de función hepática.', 'Vial reconstituido estable 12 H a T° 20 - 25 °C / Solución diluida 24 H T° 20 - 25 °C', 'dhernandez', '2024-05-28 14:20:30', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n75 mg / 7(mg/ml) = 10 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(51, 'GANCICLOVIR', 12, 16, 'Polvo liofilizado para solución inyectable 500 mg', 'Antibiótico', '10-15 mg/kg/día repartida en 2 dosis.', 'Reconstituir con 10 ml de agua estéril o SSN', 'Concentracion máxima  permitida:10 mg/ml. \r\n\r\nDiluir con DAD 5% o SSN 0,9%', '1 hora', 'Cefepima, piperacilina/tazobactam, Anfotericina B', 'La solución reconstituida en el vial es estable a temperatura ambiente durante 12 horas', '- Vigilar deshidratación y monitorizar función renal. \r\n\r\n- Produce flebitis y vigilar reacción alérgica.', 'dhernandez', '2024-05-28 14:51:24', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n75 mg / 10(mg/ml) = 7,5 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)'),
(52, 'ANFOTERCINA B DESOXICOLATO o convencional', 12, 17, 'Vial 50 mg', 'Antibiótico', '0.5-1mg/kg/día', 'IV: Agregue 10 ml de agua estéril sin conservantes a cada vial de anfotericina. Diluir aún más con dextrosa 5% o dextrosa 10% la concentración final no debe exceder 0,1 mg/ml para administración periférica. \r\n\r\nEn pacientes que no pueden tolerar un gran volumen de líquido, se puede administrar anfotericina B en una concentración final que no supere los 0,25 mg/ml en D5W o D10W a través de un catéter venoso central', 'Concentración máxima permitida: 0.2 mg /ml', 'IV: Administrar mediante infusión intravenosa durante 2 horas; para la administración se puede utilizar un filtro en línea (&amp;amp;amp;amp;gt;1 micrón de diámetro medio de poro)', 'Evalúe la posibilidad de interacciones con otros medicamentos nefrotóxicos que pueda estar tomando el paciente.', '- Guarde los viales intactos bajo refrigeración. \r\n\r\n- Los viales reconstituidos son estables, protegidos de la luz, durante 24 horas a temperatura ambiente y 1 semana cuando se refrigeran. \r\n\r\n- Los aditivos parenterales en dextrosa 5% son estables, protegidos de la luz, durante 24 horas a temperatura ambiente y 2 días en refrigeración. \r\n\r\n- La exposición a corto plazo (&amp;amp;amp;amp;lt;24 horas) a la luz durante la infusión intravenosa no afecta apreciablemente la potencia.', 'Vgilar niveles de BUM y creatinina, electrolitos, pruebas de función hepática, temperatura, controlar los signos de hipopotasemia (debilidad muscular, calambres, somnolencia, cambios en el ECG, etc.)\r\n\r\nLa infusión rápida se ha asociado con hipotensión, hipopotasemia, arritmias y shock. Evite la extravasación; puede causar irritación química; monitorear el sitio de infusión; Se puede agregar 1 unidad de heparina/1 ml de solución para perfusión para reducir la flebitis.', 'dhernandez', '2024-05-28 14:54:54', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: \r\nNiño 15 kg\r\n15 mg / 0.2 (mg/ml) = 75 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'La dilución máxima se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo:\r\nNiño de 15 kg\r\n1000 ml + (5kg x 50ml)=1250 ml / 24 horas= 52 ml (volúmen máximo de dilución)\r\n\r\nNota: En el caso de la Anfotericina B convencional, la concentración máxima permitida, supera el cálculo de la fórmula de Holliday Segar.'),
(53, 'ANFOTERICINA B COMPLEJO LIPÍDICO', 12, 17, 'Vial 100 mg/ 20 ml', 'Antibiótico', '3 -5 mg/kg/día.', 'N.A', 'Concentración máxima permitida: 1 mg/ml con dextrosa al 5%.', '2 horas', 'Aminoglucósidos, ciclosporina, pentamidina, tacrolimus, vancomicina aumentan el riesgo de nefrotoxicidad. \r\n\r\nEl uso concurrente de corticoides, corticotropina y los diuréticos (del asa y tiazidas) puede potenciar la aparición de hipopotasemia, así como la hipopotasemia inducida por anfotericina B', 'Los viales intactos deben almacenarse entre 2 °C y 8 ​​°C (35 °F a 46 °F); no congelar. Proteja los viales intactos de la exposición a la luz. Las soluciones en dextrosa 5% para infusión son estables durante 48 horas en refrigeración y durante 6 horas adicionales a temperatura ambiente.', 'Vigilar función renal, electrolitos (especialmente potasio y magnesio), pruebas de función hepática, temperatura, monitorear entradas y salidas; controlar los signos de hipopotasemia (debilidad muscular, calambres, somnolencia, cambios en el ECG, etc.)', 'dhernandez', '2024-05-28 14:57:20', 'jramos', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida (casilla anterior)\r\n\r\nEjemplo: niño 15 kg \r\n45mg / 1(mg/ml) = 45 ml (volumen de dilución mínimo)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'Se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , divido en 24 horas.\r\n\r\nEjemplo: niño de 15 Kg\r\n1000 ml x (5 kg x 50 ml)= 1250 ml / 24 horas = 52 ml (volumen máximo de dilución)'),
(54, 'ANFOTERICINA B LIPOSOMAL', 12, 17, 'Ampolla 50 mg / 10 ml', 'Antibiótico', '3-5 mg/kg/día.', 'Reconstituir en 10 ml con Solución salina 0,9%, para una concentración de 5 mg/ml', 'Concentración máxima permitida: 1 mg/ml en dextrosa 5%', '2 horas', 'Fármacos aminoglucósidos, vancomicina, antineoplásicos y flucitosina', 'Vigilar episodios eméticos, reacción de hipersensibilidad y desequilibro electrolítico (Potasio, magnesio, calcio y sodio). Nefrotoxicidad . Filtración con 1,2 y fotoprotección', 'Monitorear función renal, electrolitos (especialmente potasio y magnesio), pruebas de función hepática, temperatura, hematocrito, monitorear entradas y salidas; controlar los signos de hipopotasemia (debilidad muscular, calambres, somnolencia, cambios en el ECG, etc.); controlar la función cardíaca si se usa simultáneamente con corticosteroides.', 'dhernandez', '2024-05-28 15:00:10', '', 1, 'Se calcula dividiendo la dosis prescrita en la concentración máxima permitida\r\n\r\nEjemplo: niño de 15 kg\r\n45 mg de anfotericina B /1 (mg/ml) = 45 ml (volumen mínimo de dilución)\r\n\r\nElegir volumen mínimo o volumen máximo (casilla siguiente), dependiendo el estado del niño: restricción hídrica, balance de líquidos, riesgo de flebitis, etc.\r\n\r\nProgramar la bomba de infusión en modo alterno para evitar sobrecarga de volumen.', 'Se calcula con la fórmula de Holliday-Segar:\r\n1 a 10 kg = 100 ml /kg dividido en 24 horas\r\n11 a 20 Kg = 1000 ml por los primeros 10 kg y 50 ml por cada kilo adicional, divido en 24 horas\r\n21 kilos o más = 1500 por los primeros 20 kg y 20 ml por cada kilo adicional , dividido en 24 horas.\r\n\r\nEjemplo: niño de 15 Kg\r\n1000 ml x (5 kg x 50 ml)= 1250 ml / 24 horas = 52 ml (volumen máximo de dilución)'),
(56, 'INSULINA REGULAR', 13, 31, '100 UI/ml  10 ml', 'Control de la glucemia', 'Infusión continua: 0.05 a 0.2 UI/kg/h', 'NA', 'Concentración máxima permitida 100 UI/ml', 'Infusión continua: según dosis deseada\r\nml/h= dosis en UI x peso en kg / UI del medicamento por ml\r\n\r\nEjemplo: Usuario 15 kg dosis prescrita: 0.05 UI/kg/h\r\nml/h= 0.05x15/1 =  0.75 ml/h', 'Incompatible para paso en Y con: Dopamina.', 'Estabilidad: 6h.\r\nConservar cadena de frío\r\nSe adhiere a las paredes del equipo, se recomienda preparar la primera mezcla al doble (99 ml SSN + 1ml de Insulina R) y desechar los primeros 50 ml para saturar por completo, todo el trayecto del equipo.', 'Vigilar concentraciones séricas de potasio, causa hipopotasemia. Titulación y\r\ndescenso lento. Toma de glucometría cada hora al iniciar la infusión y cada que se modifique el\r\ngoteo, continuar tomas cada 2 a 4 horas según estabilidad metabólica.', 'jramos', '2024-11-06 15:01:24', 'jramos', 1, 'Infusión continua:\r\n&lt;10 kg:  0.3 ml (30 UI) + 99.7 ml SSN\r\nConcentración: 0.3 UI /ml\r\n&gt;10 kg:  1 ml (100 UI) + 99 ml SSN\r\nConcentración: 1 UI /ml', 'NA'),
(57, 'LEVOSIMENDAN', 13, 22, '12,5 mg / 5 ml', 'Inotrópico', 'Bolo 6 mcg/kg en 15 minutos\r\nInfusión continua: 0.05-0.2 mcg/Kg/min', 'NA', 'Concentración máxima permitida: 50 mcg/ml', 'Infusión continua: según dosis deseada\r\nml/h= dosis en mcg x peso en kg x 60 min / mcg del medicamento por ml\r\nejemplo: Usuario 15 kg dosis prescrita: 0.05 mcg/kg/min\r\nml/h= 0.05x15x60/50 = 0.9 ml/h', 'Furosemida, heparina, bicarbonato de sodio y cefalosporinas.', 'El color del concentrado puede cambiar a naranja durante el almacenamiento sin que ello signifique una pérdida de potencia.\r\nEstabilidad: 24h Mantener cadena de frío de 2-8°C', 'Si el paciente presenta baja precarga y/o resistencias vasculares periféricas bajas, puede presentarse hipotensión, especialmente si se emplea dosis bolo. Contraindicaciones: Arritmias o síndrome de Q-T largo, alergia al levosimendán, hipotensión arterial refractaria a inotrópicos, insuficiencia renal severa, insuficiencia hepática severa, resistencias vasculares periféricas disminuidas.', 'jramos', '2024-11-06 15:16:23', 'jramos', 1, 'Bolo: Diluir a una concentración máxima por ml de 50 mcg\r\nInfusión continua: \r\n&amp;lt; 10 kg: 2 ml (5 mg) + 248 ml de SSN\r\nConcentración: 20 mcg/ml\r\n&amp;gt;10 kg: 5 ml (12.5 mg) + 245 ml de SSN\r\nConcentración: 50 mcg/ml', 'NA'),
(58, 'NITROGLICERINA', 13, 31, '50 mg / 10 ml', 'Antihipertensivo y vasodilatador periférico', 'Infusión continua: 0.25 a 5 mcg/kg/min', 'NA', 'Concentración máxima permitida: 400 mcg/ml', 'Infusión continua: según dosis deseada\r\nml/h= dosis en mcg x peso en kg x 60 min / mcg del medicamento por ml\r\n\r\nEjemplo: Usuario 15 kg dosis prescrita: 0.25 mcg/kg/min\r\nml/h= 0.25x15x60/200 = 1.1 ml/h', 'Incompatible con: Heparina, furosemida, bicarbonato de sodio y alteplasa', 'Estabilidad:24h\r\nRequiere equipo fotoprotector', 'Monitoreo continuo, titulación y descenso lento. Puede producir hipotensión, bradicardia, cefalea.', 'jramos', '2024-11-06 15:29:37', 'jramos', 1, 'Infusión continua: \r\nMenores de 10 kg: 1 ml  (5 mg) + 99 ml de DAD 5%\r\nConcentración: 50 mcg/ml\r\nMayores de 10 kg: 10 ml (50 mg) + 240 ml de DAD 5%\r\nConcentración: 200 mcg/ml', 'NA'),
(59, 'VECURONIO', 13, 31, 'Liofilizado 10 mg', 'Relajante muscular', 'Bolo: 0.1 mg/kg\r\nInfusión continua: 1 a 4 mcg/kg/min', '5 ml SSN 2mg/ml', 'Concentración máxima permitida: 1 mg/ml', 'Infusión continua: según dosis deseada\r\nml/h= dosis en mcg x peso en kg x 60 min / mcg del medicamento por ml\r\n\r\nEjemplo: Usuario 15 kg dosis prescrita: 1 mcg/kg/min\r\nml/h= 1x15x60/1.000 = 0.9 ml/h', 'Furosemida, bicarbonato de sodio, heparina y morfina', 'Estabilidad: 24h', 'Monitoreo continuo, puede producir hipotensión y bradicardia. Induce paro respiratorio por bloqueo neuromuscular completo. Ajustar dosis en disfunción hepática, La administración de anestésicos, bloqueantes de canales de calcio, sales de magnesio o quinidina afectan el bloqueo neuromuscular. En caso de sobredosificación, revertir con Sugammandex (Neostigmine + atropina 1 mg/kg).', 'jramos', '2024-11-06 15:51:04', 'jramos', 1, 'Bolo: diluir en 5 a 10 ml y pasar IV directo\r\nInfusión continua:\r\nMenores de 10 kg:  20 mg en 50 ml SSN\r\nConcentración: 400 mcg/ml\r\nMayores de 10 kg: 50 mg en 50 ml SSN\r\nConcentración 1000 mcg/ml', 'NA'),
(60, 'VASOPRESINA', 13, 31, '20 UI/ml', 'Vasopresor', 'Infusión continua: 0.2 a 2 mUI/kg/min', 'NA', 'Concentración máxima permitida: 1UI/ml', 'Infusión continua: según dosis deseada\r\nml/h= dosis en UI x peso en kg / UI del medicamento por ml\r\n\r\nEjemplo: Usuario 15 kg dosis prescrita: 20 mcg/kg/min\r\nml/h= 0.2x15x60/200 = 0.9 ml/h', 'Bicarbonato, heparina, morfina, furosemida', 'Estabilidad: 18h\r\nConservar en cadena de frío', 'Monitoreo continuo, puede producir hipertensión, taquicardia y arritmias. Vigilar gasto urinario.\r\nNecrosis por extravasación, evitar su administración por vena periférica. Titulación y descenso lento.', 'jramos', '2024-11-06 16:04:36', 'jramos', 1, 'Infusión continua:\r\nMenores de 10 kg:  0.5 ml (10 UI) + 99.5 SSN\r\nConcentración: 100 mUI/ml\r\nMayores de 10 kg:  1 ml (20 UI) + 99 SSN\r\nConcentración: 200 mUI/ml', 'NA'),
(61, 'PROPOFOL', 13, 31, '1 gr en 100 ml', 'Sedante, anestésico', '0,5 a 5 mg/kg/h', 'NA', 'Concentración máxima permitida: 10 mg/ml', 'Infusión continua: según dosis deseada\r\nml/h= dosis en mg x peso en kg / mg del medicamento por ml\r\nejemplo: Usuario 15 kg dosis prescrita: 0.5 mg/kg/h\r\nml/h= 0.5x15/10 = 0.7 ml/h', 'Debe administrarse por única vía central', 'Estabilidad 12 horas', 'Monitorización continua. Produce hipotensión y bradicardia. Manipular con técnica estéril y cambiar la mezcla y el equipo cada 12 horas, la base lipída favorece el rápido crecimiento bacteriano. Administrar la infusión continua por una vía exclusiva del catéter central. De ser necesaria la infusión por vena periférica, canalizar vena de gran calibre con catéter #22 y conectar en el puerto libre de aguja del canal B de la bomba, una DAD5% a la misma velocidad de infusión que el Propofol, para evitar flebitis. Valorar signos de intoxicación por propofol (acidosis metabólica severa, hiperpotasemia, lipemia, rabdomiolisis, hepatomegalia e insuficiencia cardiaca y renal). Titulación y descenso lento.', 'jramos', '2024-11-06 16:12:55', NULL, 1, 'Infusión continua: puro\r\nConcentración: 10 mg/ml', 'NA'),
(62, 'DEXMEDETOMIDINA', 13, 31, '200 mcg/2ml', 'Sedante', '0.2 a 0.7 mcg/kg/h', 'NA', 'Concentración máxima permitida: 8 mcg/ml', 'Infusión continua: según dosis deseada\r\nml/h= dosis en mcg x peso en kg x 60 min / mcg del medicamento por ml\r\n\r\nEjemplo: Usuario 15 kg dosis prescrita: 0.2 mcg/kg/h\r\nml/h= 0.2x15/4 = 0.75 ml/h', 'Fentanyl, benzodiacepinas, morfina, anfotericina.', 'Estabilidad por 24 horas', 'Monitoreo continuo, puede producir hipotensión, oliguria, bradicardia y depresión\r\nrespiratoria. Para suspender, requiere disminución gradual de la dosis para evitar\r\nsíndrome de abstinencia. Contraindicado en bradicardia o FEVI &lt;30%.', 'jramos', '2024-11-06 16:20:31', NULL, 1, 'Infusión continua: 2 ml (200 mcg) + 48 ml SSN\r\nConcentración: 4 mcg/ml', 'NA');

